import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CampBloodDonationController = () => {
  const [donations, setDonations] = useState([]);
  const [editingDonation, setEditingDonation] = useState(null);

  useEffect(() => {
    getCampDonations();
  }, []);

  app.get("/camp/donations", async (req, res) => {
    try {
      const { campId } = req.params;
      const selectDonations = "SELECT * FROM camp_donations";
      const [donations] = await db.promise().query(selectDonations);

      res.status(200).json(donations);
    } catch (err) {
      console.error(err);
      res.status(500).json({ error: 'Internal Server Error' });
    }
  });

  const handleUpdateDonation = (donationId) => {
    const donationToUpdate = donations.find((item) => item.id === donationId);
    if (!donationToUpdate) {
      console.error('Blood donation not found for updating.');
      return;
    }

    // Set the editing donation
    setEditingDonation(donationToUpdate);
  };

  const handleCancelUpdate = () => {
    // Clear the editing donation
    setEditingDonation(null);
  };

  const handleSaveUpdate = () => {
    axios
      .put(`http://localhost:5000/camp/donations/${editingDonation.id}`, editingDonation)
      .then((response) => {
        console.log('Blood donation updated successfully:', response.data);
        // Show success toast
        toast.success('Blood donation updated successfully');
        // Refresh the entire list from the server after update
        getCampDonations();
        // Clear the editing donation
        setEditingDonation(null);
      })
      .catch((error) => {
        console.error('Error updating blood donation:', error);
        // Show error toast
        toast.error('Error updating blood donation');
      });
  };

  const handleDeleteDonation = (donationId) => {
    axios
      .delete(`http://localhost:5000/camp/donations/${donationId}`)
      .then((response) => {
        console.log('Blood donation deleted successfully:', response.data);
        // Show success toast
        toast.success('Blood donation deleted successfully');
        // Refresh the entire list from the server after deletion
        getCampDonations();
      })
      .catch((error) => {
        console.error('Error deleting blood donation:', error);
        // Show error toast
        toast.error('Error deleting blood donation');
      });
  };

  return (
    <>
      <ToastContainer />
      <div className="container mx-auto p-4">
        <table>
          <thead>
            <tr className="bg-gray-200">
              <th className="py-2 px-4 border">Donor Name</th>
              <th className="py-2 px-4 border">Blood Group</th>
              <th className="py-2 px-4 border">Donation Date</th>
              <th className="py-2 px-4 border">Donation Time</th>
              <th className="py-2 px-4 border">Blood Unit</th>
              <th className="py-2 px-4 border">Action</th>
            </tr>
          </thead>
          <tbody>
            {donations.map((donation) => (
              <tr key={donation.id} className="hover:bg-gray-100">
                <td className="py-2 px-4 border">
                  {editingDonation && editingDonation.id === donation.id ? (
                    <input
                      type="text"
                      value={editingDonation.donorName}
                      onChange={(e) => setEditingDonation({ ...editingDonation, donorName: e.target.value })}
                    />
                  ) : (
                    donation.donorName
                  )}
                </td>
                <td className="py-2 px-4 border">
                  {editingDonation && editingDonation.id === donation.id ? (
                    <input
                      type="text"
                      value={editingDonation.bloodGroup}
                      onChange={(e) => setEditingDonation({ ...editingDonation, bloodGroup: e.target.value })}
                    />
                  ) : (
                    donation.bloodGroup
                  )}
                </td>
                <td className="py-2 px-4 border">{donation.donationDate}</td>
                <td className="py-2 px-4 border">
                  {editingDonation && editingDonation.id === donation.id ? (
                    <input
                      type="text"
                      value={editingDonation.donationTime}
                      onChange={(e) => setEditingDonation({ ...editingDonation, donationTime: e.target.value })}
                    />
                  ) : (
                    donation.donationTime
                  )}
                </td>
                <td className="py-2 px-4 border">
                  {editingDonation && editingDonation.id === donation.id ? (
                    <input
                      type="text"
                      value={editingDonation.bloodUnit}
                      onChange={(e) => setEditingDonation({ ...editingDonation, bloodUnit: e.target.value })}
                    />
                  ) : (
                    donation.bloodUnit
                  )}
                </td>
                <td className="py-2 px-4 border">
                  {editingDonation && editingDonation.id === donation.id ? (
                    <>
                      <button
                        className="bg-green-500 text-white px-2 py-1 rounded mr-2"
                        onClick={handleSaveUpdate}
                      >
                        Save
                      </button>
                      <button
                        className="bg-gray-500 text-white px-2 py-1 rounded"
                        onClick={handleCancelUpdate}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        className="bg-blue-500 text-white px-2 py-1 rounded mr-2"
                        onClick={() => handleUpdateDonation(donation.id)}
                      >
                        Update
                      </button>
                      <button
                        className="bg-red-500 text-white px-2 py-1 rounded"
                        onClick={() => handleDeleteDonation(donation.id)}
                      >
                        Delete
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default CampBloodDonationController;
