import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PremiumDonorManagement = () => {
  const [premiumDonors, setPremiumDonors] = useState([]);
  const [selectedDonor, setSelectedDonor] = useState(null);
  const [updatedDonor, setUpdatedDonor] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bloodType: '',
    latitude: 0,
    longitude: 0,
    age: 0,
    address: '',
    phoneNumber: '',
    availabilityTime: '',
    DonorType: '',
  });

  useEffect(() => {
    // Fetch all premium donors on component mount
    fetchPremiumDonors();
  }, []);

  const fetchPremiumDonors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/donors');
      setPremiumDonors(response.data);
    } catch (error) {
      console.error('Error fetching premium donors:', error);
    }
  };

  const handleViewProfile = (donor) => {
    setSelectedDonor(donor);
    setUpdatedDonor({
      firstName: donor.first_name,
      lastName: donor.last_name,
      email: donor.email,
      bloodType: donor.blood_type,
      latitude: donor.latitude,
      longitude: donor.longitude,
      age: donor.age,
      address: donor.address,
      phoneNumber: donor.phone_number,
      availabilityTime: donor.availability_time,
      DonorType: donor.donor_type,
    });
  };

  const clearSelectedDonor = () => {
    setSelectedDonor(null);
    setUpdatedDonor({
      firstName: '',
      lastName: '',
      email: '',
      bloodType: '',
      latitude: 0,
      longitude: 0,
      age: 0,
      address: '',
      phoneNumber: '',
      availabilityTime: '',
      DonorType: '',
    });
  };

  const handleUpdateDonor = async () => {
    try {
      // Make API call to update donor details
      // Use updatedDonor state for the updated data
      await axios.put(`http://localhost:5000/api/donors/update/${selectedDonor.donor_id}`, updatedDonor);
      // Fetch updated donor details after the update
      await handleViewProfile(selectedDonor);
    } catch (error) {
      console.error('Error updating donor details:', error);
    }
  };

  return (
    <div className="flex">
      {/* Donor List */}
      <div className="w-1/4 p-4">
        <h2 className="text-2xl font-bold mb-4">Premium Donors</h2>
        <ul>
        {premiumDonors.map((donor) => (
  <li key={donor.donor_id} className="mb-2">
    <h3>{donor.first_name} {donor.last_name}</h3>
    <p>Email: {donor.email}</p>
    <p>Blood Type: {donor.blood_type}</p>
    <p>Latitude: {donor.latitude}</p>
    <p>Longitude: {donor.longitude}</p>
    <p>Age: {donor.age}</p>
    <p>Address: {donor.address}</p>
    <p>Phone Number: {donor.phone_number}</p>
    <p>Availability Time: {donor.availability_time}</p>
    <p>Donor Type: {donor.donor_type}</p>
    {donor.profile_picture && (
      <img
        src={`data:image/png;base64,${donor.profile_picture.toString('base64')}`}
        alt={`Profile of ${donor.first_name} ${donor.last_name}`}
        className="rounded-full h-20 w-20 object-cover"
      />
    )}
    <button
      className="ml-2 bg-blue-500 text-white px-2 py-1 rounded"
      onClick={() => handleViewProfile(donor)}
    >
      View Profile
    </button>
  </li>
))}

        </ul>
      </div>

      {/* Donor Profile and Update Form */}
      <div className="w-3/4 p-4">
        {selectedDonor ? (
          <>
            <h2 className="text-2xl font-bold mb-4">
              {selectedDonor.first_name} {selectedDonor.last_name}'s Profile
            </h2>
            <table className="w-full border border-collapse">
              <thead>
                <tr>
                  <th>Field</th>
                  <th>Value</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(selectedDonor).map(([field, value]) => (
                  <tr key={field}>
                    <td>{field}</td>
                    <td>{value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="mt-4">
              {/* Update form */}
              <h3 className="text-xl font-bold mb-2">Update Donor Details</h3>
              <form onSubmit={(e) => { e.preventDefault(); handleUpdateDonor(); }}>
                {Object.entries(updatedDonor).map(([field, value]) => (
                  <div key={field} className="mb-2">
                    <label className="block">{field}:</label>
                    <input
                      type="text"
                      value={value}
                      onChange={(e) => setUpdatedDonor({ ...updatedDonor, [field]: e.target.value })}
                      className="border p-2 w-full"
                    />
                  </div>
                ))}
                <button
                  type="submit"
                  className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                >
                  Update Donor
                </button>
              </form>
            </div>
            <button
              className="bg-gray-500 text-white px-4 py-2 rounded mt-4"
              onClick={clearSelectedDonor}
            >
              Back to Donors
            </button>
          </>
        ) : (
          <p>Select a donor to view their profile.</p>
        )}
      </div>
    </div>
  );
};

export default PremiumDonorManagement;
