import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

const PremiumDonorDetails = () => {
  const [donorDetails, setDonorDetails] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchDonorDetails = async () => {
      try {
        const response = await axios.get(`/api/donors/${id}`);
        setDonorDetails(response.data);
      } catch (error) {
        console.error('Error fetching donor details:', error);
      }
    };

    fetchDonorDetails();
  }, [id]);

  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">Premium Donor Details</h2>
      <div className="border p-4">
        <div className="mb-4">
          <strong>Name:</strong> {donorDetails.first_name} {donorDetails.last_name}
        </div>
        {/* Add more details as needed */}
        <div className="mb-4">
          <strong>Email:</strong> {donorDetails.email}
        </div>
        {/* Add more details as needed */}
        <div className="mb-4">
          <strong>Blood Type:</strong> {donorDetails.blood_type}
        </div>
        {/* Add more details as needed */}
        {/* Display other donor details */}

        <Link to="/donor-list" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700">
          Back to Donor List
        </Link>
      </div>
    </div>
  );
};

export default PremiumDonorDetails;
