import React from 'react';
import Navbar from './Navbar'; 
import Footer1 from './Footer';
import slogan1 from './../Assest/img/slogan1.png';
import slogan2 from './../Assest/img/slogan2.png';
import slogan from './../Assest/img/Slogan.jpg';
import blood1 from './../Assest/img/blood1.png';
import blood2 from './../Assest/img/blood2.png';
import blood3 from './../Assest/img/blood3.png';
import blood4 from './../Assest/img/blood4.png';

const HomePage = () => {
  const temp2 = [
    { title: 'Registration', img: blood1 },
    { title: 'Seeing', img: blood2 },
    { title: 'Donation', img: blood3 },
    { title: 'Save Life', img: blood4 },
  ];

  return (
    <>
      <Navbar />

      <div className="flex justify-between items-center dark:text-white">

<div className="w-1/2">  
  <img src={slogan1} alt="Slogan 1"/>
</div>

<div className="w-1/2">
  <img src={slogan} alt="Slogan 2"/> 
</div>

<div className="w-1/2">
  <img src={slogan2} alt="Slogan 2"/> 
</div>

</div>

        {/* Learn About Donation Section */}
        <h1 className='font-bold  font- size text-center text-blood my-4 text-lg underline'>Learn About Donation</h1>
        <div className='flex px-20'>
          <div>
            <p className='text-center'>
              <code>After donating blood, the body works to replenish the blood loss. This stimulates the production of new blood cells and in turn, helps in maintaining good health.</code>
            </p>
          </div>
          <div>
            {/* Add more content if needed */}
          </div>
        </div>

        {/* Blood Donation Process Section */}
        <p className='text-xl underline font-bold text-blood text-center mt-5 mb-5'>
          Blood Donation Process
        </p>
        <div className='grid grid-cols-2 place-items-center'>
          {temp2.map((e, i) =>
            <div key={i} className='border-metal shadow-md rounded-lg overflow-hidden max-w-[90%] select-none grid grid-cols-2'>
              <div><img src={e.img} draggable={false} width="100%" alt={`Step ${i + 1}`} /></div>
              <div className='m-4'>
                <h1 className='font-bold text-lg text-midnight dark:text-white-900'>{i + 1} - {e.title}</h1>
                <p className='text-justify'>
                  Lorem ipsum dolor, sit amet consectetur qwey adipisicing elit. Doloribus, as aliquam corporis dolorem consectetur qui libero, veritatis, nihil alias repellat quam architecto nobis laudantium ipsum nemo nesciunt quisquam est odit ad?
                </p>
              </div>
            </div>
          )}
        </div>

        {/* Footer Section */}
        <br />
        <div className='w-full bg-blood text-white-900 h-max text-sm text-center font-bold'>
          <Footer1/>
        </div>
      
    </>
  );
};

export default HomePage;
