export const UserProvider = ({ children }) => {
    const initialState = {
      isLoggedIn: false,
      userRole: null,
      username: null, // Initialize username to null
    };
  
    const userReducer = (state, action) => {
      switch (action.type) {
        case 'LOGIN':
          return {
            ...state,
            isLoggedIn: true,
            userRole: action.userRole || null,
            username: action.username || null, // Set username when logging in
          };
        case 'LOGOUT':
          return {
            ...state,
            isLoggedIn: false,
            userRole: null,
            username: null, // Clear username when logging out
          };
        default:
          return state;
      }
    };
  
    const [state, dispatch] = useReducer(userReducer, initialState);
  
    const login = (userRole, username) => {
      dispatch({ type: 'LOGIN', userRole, username }); // Pass username when logging in
    };
  
    const logout = () => {
      dispatch({ type: 'LOGOUT' });
      // Additional logout logic if needed
      // For example, clearing local storage, redirecting to a different page, etc.
    };
  
    return (
      <UserContext.Provider value={{ state, login, logout }}>
        {children}
      </UserContext.Provider>
    );
  };
