import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AllDonationsComponent = () => {
  const [donations, setDonations] = useState([]);
  const [error, setError] = useState(null);

  const fetchAllDonations = async () => {
    try {
      const response = await axios.get('http://localhost:5000/camp/:campId/donations'); // Update the endpoint as needed
      setDonations(response.data);
    } catch (err) {
      setError('Error fetching donations');
      console.error(err);
    }
  };

  useEffect(() => {
    fetchAllDonations();
  }, []);

  const handleUpdate = async (donationId) => {
    try {
      const updatedData = {
        donorName: 'Updated Donor',
        bloodGroup: 'Updated Blood Group',
        donationDate: '2024-02-21',
        donationTime: '15:30:00',
        bloodUnit: 2,
      };

      await axios.put(`http://localhost:5000/camp/:campId/donations/${donationId}`, updatedData);
      // Refresh the donations after updating
      fetchAllDonations();
    } catch (err) {
      setError('Error updating donation');
      console.error(err);
    }
  };

  const handleDelete = async (donationId) => {
    try {
      await axios.delete(`http://localhost:5000/camp/:campId/donations/${donationId}`);
      // Refresh the donations after deleting
      fetchAllDonations();
    } catch (err) {
      setError('Error deleting donation');
      console.error(err);
    }
  };

  return (
    <div>
      <h2>All Donations</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {donations.map((donation) => (
          <li key={donation.id}>
            Donor: {donation.donor_name}, Blood Group: {donation.blood_group}, Date: {donation.donation_date}, Time: {donation.donation_time}, Units: {donation.blood_unit}
            <button onClick={() => handleUpdate(donation.id)}>Update</button>
            <button onClick={() => handleDelete(donation.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllDonationsComponent;
