import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DonorStockController = () => {
  const [donorStockList, setDonorStockList] = useState([]);
  const [newDonorStock, setNewDonorStock] = useState({
    blood_group: '',
    unit: 0,
    age: 0,
    address: '',
    donor_name: '',
    donation_time: '',
  });

  useEffect(() => {
    getDonorStocks();
  }, []);

  const getDonorStocks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/login/stf/ds');
      setDonorStockList(response.data);
    } catch (error) {
      console.error('Error fetching donor stocks:', error);
    }
  };

  const handleUpdateDonorStock = (donor_id) => {
    const donorStockToUpdate = donorStockList.find((item) => item.donor_id === donor_id);
    if (!donorStockToUpdate) {
      console.error('Donor stock not found for updating.');
      return;
    }

    axios
      .put('http://localhost:5000/login/stf/ds/update', donorStockToUpdate)
      .then((response) => {
        console.log('Donor stock updated successfully:', response.data);
        getDonorStocks();
      })
      .catch((error) => console.error('Error updating donor stock:', error));
  };

  const handleInsertDonorStock = () => {
    const { blood_group, unit, age, address, donor_name, donation_time } = newDonorStock;
    if (!blood_group || !unit || !age || !address || !donor_name || !donation_time) {
      alert('All fields are required.');
      return;
    }

    setDonorStockList((prevList) => [...prevList, newDonorStock]);
    setNewDonorStock({
      blood_group: '',
      unit: 0,
      age: 0,
      address: '',
      donor_name: '',
      donation_time: '',
    });

    axios
      .post('http://localhost:5000/login/stf/ds/insert', newDonorStock)
      .then((response) => {
        console.log('Donor stock inserted successfully:', response.data);
      })
      .catch((error) => console.error('Error inserting donor stock:', error));
  };

  const handleDeleteDonorStock = (donor_id) => {
    axios
      .delete(`http://localhost:5000/login/stf/ds/delete/${donor_id}`)
      .then((response) => {
        console.log('Donor stock deleted successfully:', response.data);
        // Refresh the entire list from the server after deletion
        getDonorStocks();
      })
      .catch((error) => console.error('Error deleting donor stock:', error));
  };
  

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Donor Stock Management</h1>
      <table className="w-full border border-collapse">
        <thead>
          <tr className="bg-gray-200">
          <th className="py-2 px-4 border">Donor Name</th>
            <th className="py-2 px-4 border">Blood Group</th>
            <th className="py-2 px-4 border">Unit</th>
            <th className="py-2 px-4 border">Age</th>
            <th className="py-2 px-4 border">Address</th>
            
            <th className="py-2 px-4 border">Donation Time</th>
            <th className="py-2 px-4 border">Action</th>
          </tr>
        </thead>
        <tbody>
          {donorStockList.map((donorStock, index) => (
            <tr key={donorStock.donor_id || index} className="hover:bg-gray-100">
               <td className="py-2 px-4 border">{donorStock.donor_name}</td>
              <td className="py-2 px-4 border">{donorStock.blood_group}</td>
              <td className="py-2 px-4 border">{donorStock.unit}</td>
              <td className="py-2 px-4 border">{donorStock.age}</td>
              <td className="py-2 px-4 border">{donorStock.address}</td>
             
              <td className="py-2 px-4 border">{donorStock.donation_time}</td>
              <td className="py-2 px-4 border">
                <button
                  className="bg-blue-500 text-white px-2 py-1 rounded mr-2"
                  onClick={() => handleUpdateDonorStock(donorStock.donor_id)}
                >
                  Update
                </button>
                <button
                  className="bg-red-500 text-white px-2 py-1 rounded"
                  onClick={() => handleDeleteDonorStock(donorStock.donor_id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2 className="text-2xl font-bold my-4">Add New Donor Stock</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleInsertDonorStock();
        }}
        className="max-w-md"
      >
         <label className="block mb-2">Donor Name:</label>
        <input
          type="text"
          value={newDonorStock.donor_name}
          onChange={(e) => setNewDonorStock({ ...newDonorStock, donor_name: e.target.value })}
          className="border p-2 w-full"
        />

        <label className="block mb-2">Blood Group:</label>
        <input
          type="text"
          value={newDonorStock.blood_group}
          onChange={(e) => setNewDonorStock({ ...newDonorStock, blood_group: e.target.value })}
          className="border p-2 w-full"
        />

        <label className="block mb-2">Unit:</label>
        <input
          type="number"
          value={newDonorStock.unit}
          onChange={(e) => setNewDonorStock({ ...newDonorStock, unit: Number(e.target.value) })}
          className="border p-2 w-full"
        />

        <label className="block mb-2">Age:</label>
        <input
          type="number"
          value={newDonorStock.age}
          onChange={(e) => setNewDonorStock({ ...newDonorStock, age: Number(e.target.value) })}
          className="border p-2 w-full"
        />

        <label className="block mb-2">Address:</label>
        <input
          type="text"
          value={newDonorStock.address}
          onChange={(e) => setNewDonorStock({ ...newDonorStock, address: e.target.value })}
          className="border p-2 w-full"
        />


        <label className="block mb-2">Donation Time:</label>
        <input
          type="text"
          value={newDonorStock.donation_time}
          onChange={(e) => setNewDonorStock({ ...newDonorStock, donation_time: e.target.value })}
          className="border p-2 w-full"
        />

        <button
          type="submit"
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          Add Donor Stock
        </button>
      </form>
    </div>
  );
};

export default DonorStockController;
