import React, { useState } from 'react';
import registerImage from '../../Assest/img/Register.png';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './UserRegister.css'

const UserRegister = () => {
  const [user, setUser] = useState({
    userName: '',
    userAge: '',
    userGender: '',
    userBloodGroup: '',
    userPhone: '',
    userEmail: '',
    userAddress: '',
    userUserName: '',
    userPassword: '',
    userRole: '',
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      const res = await fetch('http://localhost:5000/register/user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(user),
      });

      if(res.ok) {
        toast.success('Registration successful!');
      } else {
        toast.error('Registration failed'); 
      }

      setUser({
        userName: '',
        userAge: '',
        userGender: '',
        userBloodGroup: '',
        userPhone: '',
        userEmail: '',
        userAddress: '',
        userUserName: '',
        userPassword: '',
        userRole: '',
      });
      setError(null);
    } catch (err) {
      setError(err.message || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  return (
    <>
      <div className="form-container">
        <div className="img">
          <img src={registerImage} alt="Register" />
        </div>
        <div className="form-content">
          <h1>User Registration</h1>
          {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}

          <form onSubmit={handleSubmit}>
            <div className="form-field">
              <label>Full Name</label>
              <input
                type="text"
                value={user.userName}
                onChange={(e) => setUser({ ...user, userName: e.target.value })}
              />
            </div>

            <div className="form-field">
              <label>Age</label>
              <input
                type="number"
                value={user.userAge}
                onChange={(e) => setUser({ ...user, userAge: e.target.value })}
              />
            </div>

            <div className="form-field">
              <label>Gender</label>
              <select
                value={user.userGender}
                onChange={(e) => setUser({ ...user, userGender: e.target.value })}
              >
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </div>

            <div className="form-field">
  <label>User Role</label>
  <select
    value={user.userRole}
    onChange={(e) => setUser({ ...user, userRole: e.target.value })}
  >
    <option value="">Select</option>
    <option value="Donor">Donor</option>
    <option value="Recipient">Recipient</option>
    {/* Add more role options if needed */}
  </select>
</div>


            <div className="form-field">
          <label>Blood Group</label>
          <select
            value={user.userBloodGroup}
            onChange={(e) => setUser({...user, userBloodGroup: e.target.value})}  
          >
            <option value="">Select</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
        </div>


            <div className="form-field">
              <label>Phone</label>
              <input
                type="text"
                value={user.userPhone}
                onChange={(e) => setUser({ ...user, userPhone: e.target.value })}
              />
            </div>

            <div className="form-field">
              <label>Email</label>
              <input
                type="email"
                value={user.userEmail}
                onChange={(e) => setUser({ ...user, userEmail: e.target.value })}
              />
            </div>

            <div className="form-field">
              <label>Address</label>
              <textarea
                value={user.userAddress}
                onChange={(e) => setUser({ ...user, userAddress: e.target.value })}
              />
            </div>

            <div className="form-field">
              <label>Username</label>
              <input
                type="text"
                value={user.userUserName}
                onChange={(e) => setUser({ ...user, userUserName: e.target.value })}
              />
            </div>

            <div className="form-field">
              <label>Password</label>
              <input
                type="password"
                value={user.userPassword}
                onChange={(e) => setUser({ ...user, userPassword: e.target.value })}
              />
            </div>

            <button className="submit-btn" disabled={loading}>
              {loading ? 'Registering...' : 'Register'}
            </button>
          </form>
          <div className="already-have-account">
            <p>Already have an account? <a href="/">Login</a></p>
          </div>

          <ToastContainer />
        </div>
      </div>
    </>
  );
}

export default UserRegister;
