import React, { useState } from 'react';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import LoginImage from '../../Assest/img/LoginPage.jpg'

function LoginPage() {

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
     try {
      const response = await fetch('/user/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        toast('Login successful!', { type: 'success' });
      } else {
        toast('Invalid credentials!', { type: 'error' });
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="bg-gray-200 h-screen flex flex-col">
      
      <div className="p-10">
        <img 
          src={LoginImage}
          alt="Login"
          className="w-48 h-48 rounded-full mx-auto shadow" 
        />
      </div>

      <div className="max-w-md mx-auto p-8 bg-white rounded-xl shadow-md">
        <h1 className="text-3xl font-medium mb-6">Login</h1>

        <form onSubmit={handleSubmit}>
          
          <div className="space-y-4">
            <input 
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="block border border-gray-200 w-full p-2 rounded focus:outline-none focus:ring-1 focus:ring-gray-600"
            />
            
            <input
              type="password" 
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)} 
              className="block border border-gray-200 w-full p-2 rounded focus:outline-none focus:ring-1 focus:ring-gray-600"
            />
          </div>

          <button
            type="submit"
            className="block bg-indigo-500 text-white mt-6 py-2 rounded-md hover:bg-indigo-600 w-full"
          >
            Login
          </button>
        </form>
      </div>

    </div>
  );
}

export default LoginPage;
