import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AllDonationsComponent = () => {
  const [donations, setDonations] = useState([]);
  const [error, setError] = useState(null);
  const [updateData, setUpdateData] = useState({
    donorName: '',
    bloodGroup: '',
    donationDate: '',
    donationTime: '',
    bloodUnit: '',
  });

  const fetchAllDonations = async () => {
    try {
      const response = await axios.get('http://localhost:5000/camp/donations'); // Update the endpoint as needed
      setDonations(response.data);
    } catch (err) {
      setError('Error fetching donations');
      console.error(err);
    }
  };

  useEffect(() => {
    fetchAllDonations();
  }, []);

  const handleUpdate = async (donationId) => {
    try {
      await axios.put(`http://localhost:5000/camp/donations/${donationId}`, updateData);
      // Refresh the donations after updating
      fetchAllDonations();
      // Reset the updateData state after updating
      setUpdateData({
        donorName: '',
        bloodGroup: '',
        donationDate: '',
        donationTime: '',
        bloodUnit: '',
      });
    } catch (err) {
      setError('Error updating donation');
      console.error(err);
    }
  };

  const handleDelete = async (donationId) => {
    const confirmDeletion = window.confirm('Are you sure you want to delete this donation?');

    if (!confirmDeletion) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/camp/donations/${donationId}`);
      // Refresh the donations after deleting
      fetchAllDonations();
    } catch (err) {
      setError('Error deleting donation');
      console.error(err);
    }
  };

  return (
    <div>
      <h2>All Donations</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {donations.map((donation) => (
          <li key={donation.id}>
            <div>
              <span>Donor:</span>
              <input
                type="text"
                value={updateData.donorName || donation.donor_name}
                onChange={(e) => setUpdateData({ ...updateData, donorName: e.target.value })}
              />
            </div>
            <div>
              <span>Blood Group:</span>
              <input
                type="text"
                value={updateData.bloodGroup || donation.blood_group}
                onChange={(e) => setUpdateData({ ...updateData, bloodGroup: e.target.value })}
              />
            </div>
            <div>
              <span>Date:</span>
              <input
                type="text" 
                value={updateData.donationDate || donation.donation_date}
                onChange={(e) => setUpdateData({ ...updateData, donationDate: e.target.value })}
              />
            </div>
            <div>
              <span>Time:</span>
              <input
                type="text" 
                value={updateData.donationTime || donation.donation_time}
                onChange={(e) => setUpdateData({ ...updateData, donationTime: e.target.value })}
              />
            </div>
            <div>
              <span>Units:</span>
              <input
                type="text" 
                value={updateData.bloodUnit || donation.blood_unit}
                onChange={(e) => setUpdateData({ ...updateData, bloodUnit: e.target.value })}
              />
            </div>
            <button onClick={() => handleUpdate(donation.id)}>Update</button>
            <button onClick={() => handleDelete(donation.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllDonationsComponent;
