import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const PremiumDonorDetails = () => {
  const { id } = useParams();
  const [donorDetails, setDonorDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDonorDetails = async () => {
      // Check if id is undefined
      if (!id) {
        setError('Donor ID is not provided');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`http://localhost:5000/api/donors/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch donor details');
        }
        const data = await response.json();
        setDonorDetails(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    // Fetch donor details based on the ID from your API or server
    fetchDonorDetails();
  }, [id]); // Dependency on 'id' ensures useEffect runs when the ID changes

  if (!id) {
    return <p>Error: Donor ID is not provided</p>;
  }

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h2>Donor Details</h2>
      <div className="col-span-1">
                <div className="text-gray-700 font-bold">Profile Picture</div>
  
                <div className="profile-picture-container" style={{   height: '100px', width: '350px' }}>
                  <img
                    src={`http://localhost:5000${donorDetails.profile_picture}`}
                    alt={`${donorDetails.first_name} $ profile picture`}
                    className="profile-picture"
                    onError={(e) => console.error('Error loading image:', e)}
                  />
                </div>
              </div>
      <p>ID: {donorDetails.premium_donor_id}</p>
      <p>First Name: {donorDetails.first_name}</p>
      <p>Last Name: {donorDetails.last_name}</p>
      <p>Email: {donorDetails.email}</p>
      <p>Blood Type: {donorDetails.blood_type}</p>
      <p>Age: {donorDetails.age}</p>
      <p>Address: {donorDetails.address}</p>
      <p>Phone Number: {donorDetails.phone_number}</p>
      <p>Availability Time: {donorDetails.availability_time}</p>
      <p>Donor Type: {donorDetails.donor_type}</p>
      

      {/* Add more details based on your premium donor data */}
    </div>
  );
};

export default PremiumDonorDetails;
