import React, { useEffect } from 'react';
import Navbar from './StaffNavbar';
import { useStaff } from './StaffContext'; // Import useStaff hook
import { Link } from 'react-router-dom'; // Import Link for navigation

const StaffHomepage = () => {
  const { state, login } = useStaff(); // Access state and login function from useStaff hook
  const { stfUserName, staffData, stfStaffType } = state;

  useEffect(() => {
    // Assuming you have a function to handle successful login and retrieve stfStaffType
    const handleLoginSuccess = (stfStaffType) => {
      login(stfStaffType); // Dispatch LOGIN action with stfStaffType
    };

    // Call handleLoginSuccess after a successful login
    handleLoginSuccess(/* pass stfStaffType obtained from response */);
  }, []); // Ensure this effect runs only once after component mount

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      <Navbar />
      <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-200">
        <div className="m-auto p-8 bg-white rounded shadow-md">
          <h1 className="text-3xl font-bold mb-4">Staff Homepage</h1>
          <div className="flex justify-between">
            <div className="w-1/2 p-4">
              <h2 className="text-xl font-semibold mb-2">Staff Information</h2>
              <p>Staff Name: {stfUserName}</p>
              <p>Staff Type: {stfStaffType}</p>
            </div>
            <div className="w-1/2 p-4 text-right bg-purple">
              <Link to="/managedonation" className="btn btn-primary">
                Manage Donations
              </Link>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default StaffHomepage;
