// UserNavbar.js
import React, { useState } from 'react';
import StaffSidebar from './StaffLinkBar';
import { NotificationsNone } from '@mui/icons-material';

const UserNavbar = () => {
  const [isSidebarVisible, setSidebarVisible] = useState(false);

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  return (
    <nav className="bg-red sticky top-0 z-50">
      <div className="container mx-auto p-3 flex items-center justify-between">
        <button
          className="custom-menu-button text-gray"
          onClick={toggleSidebar}
        >
          <FontAwesomeIcon icon={faBars} style={{ color: 'gray' }} />
        </button>

        {/* Notifications Icon */}
         <div className="relative cursor-pointer text-gray topbarIconContainer">
           <NotificationsNone className="h-6 w-6" />
           <span className="absolute top-0 right-0 w-3 h-3 bg-red text-white rounded-full flex  justify-center text-xl topIconBadge">2</span>
         </div>

      </div>
      <StaffSidebar isVisible={isSidebarVisible} toggleSidebar={toggleSidebar} />
    </nav>
  );
};

export default UserNavbar;
