import React from 'react';
import { Link } from 'react-router-dom';
import { useUser } from './Usercontext';
import UserNavbar from './UserNavbar';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHospital, faHeartbeat, faFlask } from '@fortawesome/free-solid-svg-icons';

const UserHome = () => {
  const { state } = useUser();
  const { isLoggedIn, userData } = state;

  return (
    <>
      <UserNavbar />
      <div className="container mx-auto px-4 py-8 flex flex-col justify-center items-center space-y-8">
        {isLoggedIn && (
          <div className="text-center bg-green-500 text-white py-4">
            <h1 className="text-4xl font-bold">
              Welcome, {userData.userRole || 'User'}!
            </h1>
            <p className="text-xl">
              Saving lives, one donation at a time.
            </p>
          </div>
        )}
      </div>

      {/* Hero Section */}
      <div className="flex flex-col space-y-4 text-center bg-gray-100 py-8">
        <h1 className="text-4xl font-bold text-red-500">Welcome to the Blood Donation System</h1>
        <p className="text-xl text-gray-700">
          Saving lives, one donation at a time.
        </p>  
      </div>

    

      {/* Additional Information Section */}
      <div className="flex flex-col space-y-4 text-center">
        <h2 className="text-2xl font-semibold text-gray-700">How Blood Donation Works</h2>
        <p className="text-lg text-gray-500">
          Click "Become a Donor" to learn more about the eligibility criteria and 
          donation process.
        </p>
      </div>

      {/* Additional Information Section */}
      <div className="flex flex-col space-y-4 text-center">
        <h2 className="text-2xl font-semibold text-gray-700">How Blood Donation Works</h2>
        <p className="text-lg text-gray-500">
          If you need blood, click "Request Blood" to search
          for compatible donors.
          <Link to="/become-donor" className="btn btn-primary">
            <FontAwesomeIcon icon={faHospital} className="mr-2" /> Become a Donor
          </Link>
        </p>
      </div>

      {/* Blood Bank Information Section */}
      <div className="flex flex-col space-y-2 text-center">
        <h2 className="text-2xl font-semibold text-gray-700">Find a Blood Bank</h2>
        <p className="text-lg text-gray-500">
          Search for blood banks in your area to find the nearest 
          location to donate blood or pick up blood requests.
        </p>
        <Link to="/blood-banks" className="btn btn-outline">
          <FontAwesomeIcon icon={faFlask} className="mr-2" /> Find a Blood Bank
        </Link>
      </div>
    </>
  );
}

export default UserHome;
