import React from 'react';
import { Link } from 'react-router-dom';
import { useUser } from './Usercontext'; // Import useUser hook

import UserNavbar from './UserNavbar';

// Import FontAwesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHospital, faHeartbeat, faFlask } from '@fortawesome/free-solid-svg-icons';

const UserHome = () => {
  const { state } = useUser(); // Get user state using useUser hook
  const { isLoggedIn, userData } = state; // Destructure isLoggedIn from state

  return (
    <>
      <UserNavbar />
      <div className="container mx-auto px-4 py-8 flex flex-col justify-center items-center space-y-8">
        {/* Display welcome message with user role */}
        {isLoggedIn && (
          <div className="bg-green-100 rounded-lg shadow-md p-4 text-center">
            <h1 className="text-4xl font-bold text-red-500">
              Welcome, {userData.userRole || 'User'}!
            </h1>
            <p className="text-xl text-gray-700">
              Saving lives, one donation at a time.
            </p>
          </div>
        )}
      </div>

      {/* Hero Section */}
      <div className="flex flex-col space-y-4 text-center">
        <h1 className="text-4xl font-bold text-red-500">Welcome to the Blood Donation System</h1>
        <p className="text-xl text-gray-700">Saving lives, one donation at a time.</p>
      </div>

      {/* Call to Action Buttons */}
      <div className="flex flex-col space-y-4 md:flex md:space-y-0 md:space-x-4">
        {/* Become a Donor Button */}
        {/* ... */}
      </div>

      {/* Additional Information Section */}
      <div className="flex flex-col space-y-4 text-center">
        <h2 className="text-2xl font-semibold text-gray-700">How Blood Donation Works</h2>
        <p className="text-lg text-gray-500">
          Click "Become a Donor" to learn more about the eligibility criteria and donation process.
          {/* Request Blood Button */}
          <Link to="/request-blood" className="btn btn-primary">
            <FontAwesomeIcon icon={faHeartbeat} className="mr-2" /> Request Blood
          </Link>
        </p>
      </div>

      {/* Additional Information Section */}
      <div className="flex flex-col space-y-4 text-center">
        <h2 className="text-2xl font-semibold text-gray-700">How Blood Donation Works</h2>
        <p className="text-lg text-gray-500">
          If you need blood, click "Request Blood" to search for compatible donors.

          <Link to="/become-donor" className="btn btn-outline">
            <FontAwesomeIcon icon={faHospital} className="mr-2" /> Become a Donor
          </Link>
        </p>
      </div>

      {/* Blood Bank Information Section */}
      <div className="flex flex-col space-y-2 text-center">
        <h2 className="text-2xl font-semibold text-gray-700">Find a Blood Bank</h2>
        <p className="text-lg text-gray-500">
          Search for blood banks in your area to find the nearest location to donate blood or pick up blood requests.
          <Link to="/blood-banks" className="btn btn-primary">
            <FontAwesomeIcon icon={faFlask} className="mr-2" /> Find a Blood Bank
          </Link>
        </p>
      </div>
    </>
  );
};

export default UserHome;
