import React, { createContext, useReducer, useContext } from 'react';

const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const initialState = {
    isLoggedIn: false,
    userRole: null,
    username: null, // Add username property to the initial state
  };

  const userReducer = (state, action) => {
    switch (action.type) {
      case 'LOGIN':
        return {
          ...state,
          isLoggedIn: true,
          userRole: action.userRole || null,
          username: action.username || null, // Set username when logging in
        };
      case 'LOGOUT':
        return {
          ...state,
          isLoggedIn: false,
          userRole: null,
          username: null, // Clear username when logging out
        };
      default:
        return state;
    }
  };

  const [state, dispatch] = useReducer(userReducer, initialState);

  const login = (userRole, username) => { // Include username in the login action
    dispatch({ type: 'LOGIN', userRole, username });
  };

  const logout = () => {
    dispatch({ type: 'LOGOUT' });

    // Additional logout logic if needed
    // For example, clearing local storage, redirecting to a different page, etc.
    localStorage.removeItem('token'); // Clear token from local storage
    window.location.href = '/user/login'; // Redirect to login page
  };

  return (
    <UserContext.Provider value={{ state, login, logout }}>
      {children}
    </UserContext.Provider>
  );
};

export const useUser = () => {
  const context = useContext(UserContext);

  if (!context) {
    throw new Error('useUser must be used within a UserProvider');
  }

  return context;
};
