import React from 'react';
import { Link } from 'react-router-dom';
import { useUser } from './Usercontext'; // Import useUser hook

import UserNavbar from './UserNavbar';

// Import blood type icons from Material UI

// Import FontAwesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHospital, faHeartbeat, faFlask } from '@fortawesome/free-solid-svg-icons';

const UserHome = () => {
  const { state } = useUser(); // Get user state using useUser hook
  const { isLoggedIn, userRole } = state; // Destructure isLoggedIn and userRole from state

  return (
    <>
      <UserNavbar />
      <div className="container mx-auto px-4 py-16 flex flex-col justify-center items-center space-y-8">
        {/* Display welcome message with user role */}
        {isLoggedIn && (
          <div className="text-center">
            <h1 className="text-4xl font-bold text-red-500">
              Welcome, {userRole}!
            </h1>
            {/* You can replace "userRole" with the actual user's name property */}
            {/* For example, if the user's name property is stored as "userName", use {userName} */}
            {/* Make sure to update the user object with the actual user data */}
            <p className="text-xl text-gray-700">
              Saving lives, one donation at a time.
            </p>
          </div>
        )}

        {/* Hero Section */}
        <div className="flex flex-col space-y-4 text-center">
          <h1 className="text-4xl font-bold text-red-500">
            Welcome to the Blood Donation System
          </h1>
          <p className="text-xl text-gray-700">
            Saving lives, one donation at a time.
          </p>
        </div>

        {/* ... (rest of your code) */}
      </div>
    </>
  );
};

export default UserHome;
