// UserHome.js
import React from 'react';
import { useUser } from './Usercontext'; // Adjust the path accordingly

const UserHome = () => {
  const { state } = useUser();
  const { isLoggedIn, userRole } = state;

  return (
    <div>
      {isLoggedIn && (
        <div className="text-center">
          <h1 className="text-4xl font-bold text-red-500">
            Welcome, {userRole}!
          </h1>
          {/* Rest of the welcome message */}
        </div>
      )}
      {/* Rest of your UserHome component */}
    </div>
  );
};

export default UserHome;
