import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CampBloodDonation = () => {
  const [donations, setDonations] = useState([]);

  useEffect(() => {
    fetchDonations();
  }, []);

  const fetchDonations = async () => {
    try {
      const response = await axios.get('/camp/donations'); // Adjust the endpoint accordingly
      setDonations(response.data);
    } catch (error) {
      console.error('Error fetching donations:', error);
    }
  };

  const handleUpdateDonation = async (donationId, updatedData) => {
    try {
      await axios.put(`/camp/donations/${donationId}`, updatedData);
      toast.success('Blood donation updated successfully');
      fetchDonations(); // Refresh donation list
    } catch (error) {
      console.error('Error updating donation:', error);
      toast.error('Failed to update blood donation');
    }
  };

  const handleDeleteDonation = async (donationId) => {
    try {
      await axios.delete(`/camp/donations/${donationId}`);
      toast.success('Blood donation deleted successfully');
      fetchDonations(); // Refresh donation list
    } catch (error) {
      console.error('Error deleting donation:', error);
      toast.error('Failed to delete blood donation');
    }
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-2xl font-bold mt-5 mb-3">Blood Donations</h1>
      <div className="grid grid-cols-4 gap-4">
        {donations.map((donation) => (
          <div key={donation.id} className="border p-4">
            <p><strong>Donor Name:</strong> {donation.donor_name}</p>
            <p><strong>Blood Group:</strong> {donation.blood_group}</p>
            <p><strong>Date:</strong> {donation.donation_date}</p>
            <p><strong>Time:</strong> {donation.donation_time}</p>
            <p><strong>Unit:</strong> {donation.blood_unit}</p>
            <button className="bg-blue-500 text-white px-3 py-1 rounded mt-2" onClick={() => handleUpdateDonation(donation.id, {/* updated data here */})}>Edit</button>
            <button className="bg-red-500 text-white px-3 py-1 rounded mt-2 ml-2" onClick={() => handleDeleteDonation(donation.id)}>Delete</button>
          </div>
        ))}
      </div>
      <ToastContainer />
    </div>
  );
};

export default CampBloodDonation;
