import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BloodStock = () => {
  const [stocks, setStocks] = useState([]);
  const [newStock, setNewStock] = useState({
    name: '',
    blood_group: '',
    unit: 0,
    donor_name: '',
    donation_time: '',
    blood_status: '',
  });
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  useEffect(() => {
    getStocks();
  }, []);

  const getStocks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/login/stf/bs');
      setStocks(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const addStock = async () => {
    try {
      await axios.post('http://localhost:5000/login/stf/bs/insert', newStock);
      getStocks();
      setNewStock({
        blood_group: '',
        unit: 0,
        donor_name: '',
        donation_time: '',
        blood_status: '',
      });
      setShowSuccessMessage(true);
      setTimeout(() => {
        setShowSuccessMessage(false);
      }, 3000); // Hide the success message after 3 seconds
    } catch (error) {
      console.log(error);
    }
  };

  const updateStock = async (b_id, unitUpdate) => {
    try {
      await axios.put('http://localhost:5000/login/stf/bs/update', {
        b_id,
        blood_group: '',
        unitUpdate,
        donor_name: '',
        donation_time: '',
        blood_status: '',
      });
      getStocks();
    } catch (error) {
      console.log(error);
    }
  };

  const deleteStock = async (b_id) => {
    try {
      await axios.delete(`http://localhost:5000/login/stf/bs/delete/${b_id}`);
      getStocks();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold my-4">Blood Stock Management</h1>

      {showSuccessMessage && (
        <div className="bg-green-200 text-green-800 p-2 mb-4 rounded">
          Stock added successfully!
        </div>
      )}

<table className="table-auto border-collapse border border-slate-500">
  <thead>
    <tr>
      <th className="border border-slate-600 px-4 py-2">Blood Group</th>
      <th className="border border-slate-600 px-4 py-2">Donor</th>
      <th className="border border-slate-600 px-4 py-2">Units</th>
      <th className="border border-slate-600 px-4 py-2">Donated On</th>
      <th className="border border-slate-600 px-4 py-2">Status</th>
      <th className="border border-slate-600 px-4 py-2">Actions</th>
    </tr>
  </thead>
  <tbody>
    {stocks.map(stock => (
      <tr key={stock.b_id}>
        <td className="border border-slate-700 px-4 py-2">{stock.blood_group}</td>
        <td className="border border-slate-700 px-4 py-2">{stock.donor_name}</td>
        <td className="border border-slate-700 px-4 py-2">{stock.unit}</td>
        <td className="border border-slate-700 px-4 py-2">{stock.donation_time}</td>
        <td className="border border-slate-700 px-4 py-2">{stock.blood_status}</td>
        <td className="border border-slate-700 px-4 py-2">
          <button
            className="bg-yellow-500 px-4 py-2 rounded inline-block mr-2"
            onClick={() => updateStock(stock.b_id, stock.unit + 10)}
          >
            Add 10 Units  
          </button>
          
          <button
            className="bg-red-500 px-4 py-2 rounded inline-block"
            onClick={() => deleteStock(stock.b_id)}  
          >
            Delete
          </button>
        </td>
      </tr>
    ))}
  </tbody>
</table>

      <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 mt-8">
        <h2 className="text-lg font-medium mb-4">Add Stock</h2>

        <form onSubmit={(e) => { e.preventDefault(); addStock(); }} className="space-y-4">

  <label className="block text-gray-700 font-bold mb-2">Blood Group:</label>
  <input
    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
    type="text"
    value={newStock.blood_group}
    onChange={(e) => setNewStock({ ...newStock, blood_group: e.target.value })}
  />

  <label className="block text-gray-700 font-bold mb-2">Unit:</label>
  <input
    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
    type="number"
    value={newStock.unit}
    onChange={(e) => setNewStock({ ...newStock, unit: e.target.value })}
  />

  <label className="block text-gray-700 font-bold mb-2">Donor Name:</label>
  <input
    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
    type="text"
    value={newStock.donor_name}
    onChange={(e) => setNewStock({ ...newStock, donor_name: e.target.value })}
  />

  <label className="block text-gray-700 font-bold mb-2">Donation Time:</label>
  <input
    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
    type="text"
    value={newStock.donation_time}
    onChange={(e) => setNewStock({ ...newStock, donation_time: e.target.value })}
  />

  <label className="block text-gray-700 font-bold mb-2">Blood Status:</label>
  <input
    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
    type="text"
    value={newStock.blood_status}
    onChange={(e) => setNewStock({ ...newStock, blood_status: e.target.value })}
  />


  <button
    type="submit"
    className="bg-blue-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
  >
    Add Stock
  </button>
</form>

      </div>
    </div>
  );
};

export default BloodStock;
