import { useState } from 'react';
import { useQuery } from 'react-query';
import axios from 'axios';

export default function CampDonations() {
  const [editingDonation, setEditingDonation] = useState(null);

  const { data: donations, isLoading, refetch } = useQuery(
    'donations',
    async () => {
      const response = await axios.get('/api/donations');
      return response.data;
    }
  );

  const handleEditDonation = (donation) => {
    setEditingDonation(donation);
  };

  const handleSaveDonation = async (updatedDonation) => {
    await axios.put(`/api/donations/${updatedDonation.id}`, updatedDonation);
    setEditingDonation(null);
    refetch();
  };

  if (isLoading) return <p>Loading...</p>;

  return (
    <table>
      <thead>
        <tr>
          <th>Donor Name</th>
          <th>Blood Group</th>
          <th>Donation Date</th>
          <th>Donation Time</th>
          <th>Blood Unit</th>
          <th>Actions</th>
        </tr>
      </thead>

      {donations.map((donation) => (
        <tr key={donation.id}>
          <td>
            {editingDonation?.id === donation.id ? (
              <input
                value={editingDonation.donorName}
                onChange={(e) =>
                  handleEditDonation({
                    ...editingDonation,
                    donorName: e.target.value,
                  })
                }
              />
            ) : (
              donation.donorName
            )}
          </td>

          <td>
            {editingDonation?.id === donation.id ? (
              <select
                value={editingDonation.bloodGroup}
                onChange={(e) =>
                  handleEditDonation({
                    ...editingDonation,
                    bloodGroup: e.target.value,
                  })
                }
              >
                <option>A+</option>
                <option>B+</option>
                {/* Add other options */}
              </select>
            ) : (
              donation.bloodGroup
            )}
          </td>

          <td>
            {editingDonation?.id === donation.id ? (
              <input
                type="date"
                value={editingDonation.donationDate}
                onChange={(e) =>
                  handleEditDonation({
                    ...editingDonation,
                    donationDate: e.target.value,
                  })
                }
              />
            ) : (
              donation.donationDate
            )}
          </td>

          <td>
            {editingDonation?.id === donation.id ? (
              <input
                type="time"
                value={editingDonation.donationTime}
                onChange={(e) =>
                  handleEditDonation({
                    ...editingDonation,
                    donationTime: e.target.value,
                  })
                }
              />
            ) : (
              donation.donationTime
            )}
          </td>

          <td>
            {editingDonation?.id === donation.id ? (
              <input
                type="number"
                value={editingDonation.bloodUnit}
                onChange={(e) =>
                  handleEditDonation({
                    ...editingDonation,
                    bloodUnit: e.target.value,
                  })
                }
              />
            ) : (
              donation.bloodUnit
            )}
          </td>

          <td>
            {editingDonation?.id === donation.id ? (
              <button onClick={() => handleSaveDonation(editingDonation)}>
                Save
              </button>
            ) : (
              <button onClick={() => handleEditDonation(donation)}>Edit</button>
            )}
          </td>
        </tr>
      ))}
    </table>
  );
}
