import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const DonorDetails = () => {
  const { id } = useParams();
  const [donorDetails, setDonorDetails] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDonorDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/donors/${id}`);
        setDonorDetails(response.data);
      } catch (error) {
        console.error('Error fetching donor details:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDonorDetails();
  }, [id]);

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Donor Details</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {Object.keys(donorDetails).length === 0 ? (
            <p>No details available for this donor.</p>
          ) : (
            <div>
              <h3>Full Name: {donorDetails.first_name} {donorDetails.last_name}</h3>
              <p>Email: {donorDetails.email}</p>
              {/* Add other donor details here */}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default DonorDetails;
