import React, { useState } from 'react';
import axios from 'axios';

const BloodStockForm = () => {
  const [formData, setFormData] = useState({
    blood_group: '',
    total_unit: '',
    current_stock: '',
    blood_status: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/login/stf/inv/insert', formData);
      console.log('Blood stock inserted successfully:', response.data);
      // Handle success, e.g., show a success message to the user
    } catch (error) {
      console.error('Error during blood stock insertion:', error);
      // Handle error, e.g., show an error message to the user
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-4 bg-gray-200 rounded-md">
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-600">Blood Group</label>
          <input
            type="text"
            name="blood_group"
            value={formData.blood_group}
            onChange={handleChange}
            className="w-full border rounded-md px-2 py-1 focus:outline-none focus:ring focus:ring-blue-500 focus:ring-opacity-50"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-600">Total Unit</label>
          <input
            type="text"
            name="total_unit"
            value={formData.total_unit}
            onChange={handleChange}
            className="w-full border rounded-md px-2 py-1 focus:outline-none focus:ring focus:ring-blue-500 focus:ring-opacity-50"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-600">Current Stock</label>
          <input
            type="text"
            name="current_stock"
            value={formData.current_stock}
            onChange={handleChange}
            className="w-full border rounded-md px-2 py-1 focus:outline-none focus:ring focus:ring-blue-500 focus:ring-opacity-50"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-600">Blood Status</label>
          <input
            type="text"
            name="blood_status"
            value={formData.blood_status}
            onChange={handleChange}
            className="w-full border rounded-md px-2 py-1 focus:outline-none focus:ring focus:ring-blue-500 focus:ring-opacity-50"
            required
          />
        </div>

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-300 focus:ring-opacity-50"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default BloodStockForm;
