import React, { useState } from 'react';
import axios from 'axios';
import { useUser } from './Usercontext'; // Import the UserProvider context
import { toast } from 'react-toastify'; // Import react-toastify
import 'react-toastify/dist/ReactToastify.css'; // Import react-toastify styles

const BloodRequestForm = () => {
  const { state: { userRole }, logout } = useUser(); // Extract userRole from the UserProvider context
  const [bloodGroup, setBloodGroup] = useState('');
  const [unit, setUnit] = useState(0);
  const [patientName, setPatientName] = useState('');
  const [patientAddress, setPatientAddress] = useState('');
  const [patientContact, setPatientContact] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Check if user is authenticated and has the role of 'Recipient'
      if (userRole !== 'Recipient') {
        toast.error('Permission denied. Only recipients can request blood.');
        return;
      }

      // Assuming you have stored the JWT token in localStorage after login
      const token = localStorage.getItem('token');
      
      const response = await axios.post(
        'http://localhost:5000/login/stf/request',
        {
          bloodGroup,
          unit,
          patientName,
          patientAddress,
          patientContact
        },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Handle successful response
      console.log(response.data);
      toast.success('Blood request submitted successfully!');
    } catch (error) {
      // Handle errors
      console.error('Error requesting blood:', error);
      
      // If token is invalid or expired, log the user out
      if (error.response.status === 401) {
        logout();
      }

      // Display error message using Toastify
      toast.error(error.response.data.message);
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4">Blood Donation Request</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="bloodGroup" className="block text-sm font-medium text-gray-600">Blood Group:</label>
          <input type="text" id="bloodGroup" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)} className="input-field" />
        </div>
        <div>
          <label htmlFor="unit" className="block text-sm font-medium text-gray-600">Unit:</label>
          <input type="number" id="unit" value={unit} onChange={(e) => setUnit(e.target.value)} className="input-field" />
        </div>
        <div>
          <label htmlFor="patientName" className="block text-sm font-medium text-gray-600">Patient Name:</label>
          <input type="text" id="patientName" value={patientName} onChange={(e) => setPatientName(e.target.value)} className="input-field" />
        </div>
        <div>
          <label htmlFor="patientAddress" className="block text-sm font-medium text-gray-600">Patient Address:</label>
          <input type="text" id="patientAddress" value={patientAddress} onChange={(e) => setPatientAddress(e.target.value)} className="input-field" />
        </div>
        <div>
          <label htmlFor="patientContact" className="block text-sm font-medium text-gray">Patient Contact:</label>
          <input type="text" id="patientContact" value={patientContact} onChange={(e) => setPatientContact(e.target.value)} className="input-field" />
        </div>
        <button type="submit" className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75">Submit</button>
      </form>
    </div>
  );
};

export default BloodRequestForm;
