import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';
import './Style.css'; // Import Tailwind CSS file

const PremiumDonorsMap = () => {
  const [donorsLocations, setDonorsLocations] = useState([]);

  useEffect(() => {
    // Fetch premium donors' locations from the server
    axios.get('/api/premium-donors/locations')
      .then(response => setDonorsLocations(response.data))
      .catch(error => console.error('Error fetching premium donors locations:', error));
  }, []);

  // Define default latitude and longitude here
  const defaultLat = 0; // Replace with your actual default latitude
  const defaultLng = 0; // Replace with your actual default longitude

  return (
    <div className="map-container">
      <h2>Premium Donors Map</h2>
      <MapContainer center={[defaultLat, defaultLng]} zoom={13} style={{ height: '100%', width: '100%' }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        {donorsLocations.map(donor => (
          <Marker key={donor.id} position={[donor.latitude, donor.longitude]}>
            <Popup>
              <strong>{donor.userName}</strong><br />
              Phone: {donor.userPhone}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default PremiumDonorsMap;
