import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BloodStock = () => {
  const [stocks, setStocks] = useState([]);
  const [newStock, setNewStock] = useState({
    name: '',
    blood_type: '',
    blood_group: '',
    unit: 0,
    donor_name: '',
    donation_time: '',
    blood_status: '',
    new_field: '',
  });

  useEffect(() => {
    getStocks();
  }, []);

  const getStocks = async () => {
    try {
      const response = await axios.get('/login/emp/ub');
      setStocks(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const addStock = async () => {
    try {
      await axios.post('/login/emp/ub/insert', newStock);
      getStocks();
    } catch (error) {
      console.log(error);
    }
  };

  const updateStock = async (b_id, unitUpdate) => {
    try {
      await axios.put('/login/emp/ub/update', {
        b_id,
        name: '',
        blood_type: '',
        blood_group: '',
        unitUpdate,
        donor_name: '',
        donation_time: '',
        blood_status: '',
        new_field: '',
      });
      getStocks();
    } catch (error) {
      console.log(error);
    }
  };

  const deleteStock = async (b_id) => {
    try {
      await axios.delete(`/login/emp/ub/delete/${b_id}`);
      getStocks();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="container mx-auto px-4">
      <h1 className="text-3xl font-bold my-4">Blood Stock Management</h1>

      <div className="grid grid-cols-4 gap-4 mt-4">
        {stocks.map((stock) => (
          <div key={stock.b_id} className="bg-gray-100 p-4 rounded">
            <p><strong>Name:</strong> {stock.name}</p>
            <p><strong>Blood Type:</strong> {stock.blood_type}</p>
            <p><strong>Blood Group:</strong> {stock.blood_group}</p>
            <p><strong>Donor:</strong> {stock.donor_name}</p>
            <p><strong>Donated:</strong> {stock.donation_time}</p>

            <button
              className="bg-yellow-500 px-4 py-2 rounded inline-block mt-4 mr-2"
              onClick={() => updateStock(stock.b_id, stock.unit + 10)}
            >
              +10 Units
            </button>

            <button
              className="bg-red-500 px-4 py-2 rounded inline-block mt-4"
              onClick={() => deleteStock(stock.b_id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      <h2 className="mt-8">Add Stock</h2>
      <form onSubmit={(e) => { e.preventDefault(); addStock(); }}>
        <label>Name:</label>
        <input type="text" value={newStock.name} onChange={(e) => setNewStock({ ...newStock, name: e.target.value })} />

        <label>Blood Type:</label>
        <input type="text" value={newStock.blood_type} onChange={(e) => setNewStock({ ...newStock, blood_type: e.target.value })} />

        <label>Blood Group:</label>
        <input type="text" value={newStock.blood_group} onChange={(e) => setNewStock({ ...newStock, blood_group: e.target.value })} />

        <label>Unit:</label>
        <input type="number" value={newStock.unit} onChange={(e) => setNewStock({ ...newStock, unit: e.target.value })} />

        <label>Donor Name:</label>
        <input type="text" value={newStock.donor_name} onChange={(e) => setNewStock({ ...newStock, donor_name: e.target.value })} />

        <label>Donation Time:</label>
        <input type="text" value={newStock.donation_time} onChange={(e) => setNewStock({ ...newStock, donation_time: e.target.value })} />

        <label>Blood Status:</label>
        <input type="text" value={newStock.blood_status} onChange={(e) => setNewStock({ ...newStock, blood_status: e.target.value })} />

        <label>New Field:</label>
        <input type="text" value={newStock.new_field} onChange={(e) => setNewStock({ ...newStock, new_field: e.target.value })} />

        <button type="submit">Add Stock</button>
      </form>
    </div>
  );
};

export default BloodStock;
