import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CampBloodDonation = () => {
  const [donations, setDonations] = useState([]);
  const [editableDonations, setEditableDonations] = useState({});

  useEffect(() => {
    fetchDonations();
  }, []);

  const fetchDonations = async () => {
    try {
      const response = await axios.get('/camp/donations');
      setDonations(response.data);
      // Initialize editableDonations state based on the fetched donations
      const initialEditableState = {};
      response.data.forEach((donation) => {
        initialEditableState[donation.id] = false;
      });
      setEditableDonations(initialEditableState);
    } catch (error) {
      console.error('Error fetching donations:', error);
    }
  };

  const handleUpdateDonation = async (donationId, updatedData) => {
    try {
      await axios.put(`/camp/donations/${donationId}`, updatedData);
      toast.success('Blood donation updated successfully');
      fetchDonations(); // Refresh donation list
      setEditableDonations({ ...editableDonations, [donationId]: false }); // Exit edit mode
    } catch (error) {
      console.error('Error updating donation:', error);
      toast.error('Failed to update blood donation');
    }
  };

  const handleDeleteDonation = async (donationId) => {
    try {
      await axios.delete(`/camp/donations/${donationId}`);
      toast.success('Blood donation deleted successfully');
      fetchDonations(); // Refresh donation list
    } catch (error) {
      console.error('Error deleting donation:', error);
      toast.error('Failed to delete blood donation');
    }
  };

  const handleEditClick = (donationId) => {
    setEditableDonations({ ...editableDonations, [donationId]: true });
  };

  const handleCancelEdit = (donationId) => {
    setEditableDonations({ ...editableDonations, [donationId]: false });
  };

  const handleInputChange = (event, field, donationId) => {
    const updatedData = {
      ...donations.find((donation) => donation.id === donationId),
      [field]: event.target.value,
    };
    setDonations(
      donations.map((donation) =>
        donation.id === donationId ? updatedData : donation
      )
    );
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-2xl font-bold mt-5 mb-3">Blood Donations</h1>
      <div className="grid grid-cols-4 gap-4">
        {donations.map((donation) => (
          <div key={donation.id} className="border p-4">
            <p>
              <strong>Donor Name:</strong>
              {editableDonations[donation.id] ? (
                <input
                  type="text"
                  defaultValue={donation.donor_name}
                  onChange={(e) =>
                    handleInputChange(e, 'donor_name', donation.id)
                  }
                />
              ) : (
                donation.donor_name
              )}
            </p>
            <p>
              <strong>Blood Group:</strong>
              {editableDonations[donation.id] ? (
                <input
                  type="text"
                  defaultValue={donation.blood_group}
                  onChange={(e) =>
                    handleInputChange(e, 'blood_group', donation.id)
                  }
                />
              ) : (
                donation.blood_group
              )}
            </p>
            <p>
              <strong>Date:</strong>
              {editableDonations[donation.id] ? (
                <input
                  type="text"
                  defaultValue={new Date(donation.donation_date).toLocaleDateString()}
                  onChange={(e) =>
                    handleInputChange(e, 'donation_date', donation.id)
                  }
                />
              ) : (
                new Date(donation.donation_date).toLocaleDateString()
              )}
            </p>
            <p>
              <strong>Time:</strong>
              {editableDonations[donation.id] ? (
                <input
                  type="text"
                  defaultValue={donation.donation_time}
                  onChange={(e) =>
                    handleInputChange(e, 'donation_time', donation.id)
                  }
                />
              ) : (
                donation.donation_time
              )}
            </p>
            <p>
              <strong>Unit:</strong>
              {editableDonations[donation.id] ? (
                <input
                  type="text"
                  defaultValue={donation.blood_unit}
                  onChange={(e) =>
                    handleInputChange(e, 'blood_unit', donation.id)
                  }
                />
              ) : (
                donation.blood_unit
              )}
            </p>
            {editableDonations[donation.id] ? (
              <>
                <button
                  className="bg-blue-500 text-white px-3 py-1 rounded mt-2"
                  onClick={() =>
                    handleUpdateDonation(
                      donation.id,
                      donations.find((don) => don.id === donation.id)
                    )
                  }
                >
                  Save
                </button>
                <button
                  className="bg-gray-500 text-white px-3 py-1 rounded mt-2 ml-2"
                  onClick={() => handleCancelEdit(donation.id)}
                >
                  Cancel
                </button>
              </>
            ) : (
              <>
                <button
                  className="bg-blue-500 text-white px-3 py-1 rounded mt-2"
                  onClick={() => handleEditClick(donation.id)}
                >
                  Edit
                </button>
                <button
                  className="bg-red-500 text-white px-3 py-1 rounded mt-2 ml-2"
                  onClick={() => handleDeleteDonation(donation.id)}
                >
                  Delete
                </button>
              </>
            )}
          </div>
        ))}
      </div>
      <ToastContainer />
    </div>
  );
};

export default CampBloodDonation;
