import axios from "axios";
import React, { useState } from "react";
import Navbar from "./StaffNavbar";
import { toast } from "sonner";

const AddDonor = () => {
  const [newDonorStock, setNewDonorStock] = useState({
    blood_group: "",
    unit: 0,
    age: 0,
    address: "",
    donor_name: "",
    donation_time: "",
  });

  const [error, setError] = useState("");

  const handleInsertDonorStock = () => {
    const { blood_group, unit, age, address, donor_name, donation_time } =
      newDonorStock;
    if (!blood_group || !unit || !age || !address || !donor_name || !donation_time) {
      setError("Please fill in all fields.");
      return;
    }

    axios
      .post("http://localhost:5000/login/stf/ds/insert", newDonorStock)
      .then((response) => {
        console.log("Donor stock inserted successfully:", response.data);
        toast.success("Donor added successfully");
        setError("");
        setNewDonorStock({
          blood_group: "",
          unit: 0,
          age: 0,
          address: "",
          donor_name: "",
          donation_time: "",
        });
      })
      .catch((error) => {
        console.error("Error inserting donor stock:", error);
        setError("Error adding donor. Please try again later.");
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewDonorStock({ ...newDonorStock, [name]: value });
  };

  const handleDateChange = (e) => {
    setNewDonorStock({ ...newDonorStock, donation_time: e.target.value });
  };

  const handleReset = () => {
    setNewDonorStock({
      blood_group: "",
      unit: 0,
      age: 0,
      address: "",
      donor_name: "",
      donation_time: "",
    });
    setError("");
  };

  return (
    <>
      <Navbar />
      <div className="flex justify-between items-center px-4 py-2">
        <div className="hero-content max-w-2xl mx-auto">
          <h1 className="hero-title text-4xl md:text-5xl font-bold mb-4">
            Donor Management
          </h1>
          <p className="text-lg md:text-xl">
            <em>Empowering Lives, Saving Futures</em>
          </p>
        </div>
      </div>
      <hr className="border-t border-gray-500 mx-4" />
      <form className="p-10 bg-gray-200" onSubmit={(e) => e.preventDefault()}>
        {error && <p className="text-red-500">{error}</p>}
        <div className="flex flex-wrap justify-center">
          <div className="w-full sm:w-1/2 p-4">
            <div className="mb-4">
              <label htmlFor="donor_name" className="font-bold text-red">
                Name:
              </label>
              <input
                type="text"
                name="donor_name"
                value={newDonorStock.donor_name}
                onChange={handleChange}
                className="input-field"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="blood_group" className="font-bold text-red">
                Blood Group:
              </label>
              <select
                name="blood_group"
                value={newDonorStock.blood_group}
                onChange={handleChange}
                className="input-field"
              >
                <option value="">Select Blood Group</option>
                <option value="A+">A+</option>
                <option value="A-">A-</option>
                <option value="B+">B+</option>
                <option value="B-">B-</option>
                <option value="AB+">AB+</option>
                <option value="AB-">AB-</option>
                <option value="O+">O+</option>
                <option value="O-">O-</option>
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="unit" className="font-bold text-red">
                Unit:
              </label>
              <input
                type="number"
                name="unit"
                value={newDonorStock.unit}
                onChange={handleChange}
                className="input-field"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="age" className="font-bold text-red">
                Age:
              </label>
              <input
                type="number"
                name="age"
                value={newDonorStock.age}
                onChange={handleChange}
                className="input-field"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="address" className="font-bold text-red">
                Address:
              </label>
              <input
                type="text"
                name="address"
                value={newDonorStock.address}
                onChange={handleChange}
                className="input-field"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="donation_time" className="font-bold text-red">
                Donation Time:
              </label>
              <input
                type="datetime-local"
                name="donation_time"
                value={newDonorStock.donation_time}
                onChange={handleDateChange}
                className="input-field"
              />
            </div>
          </div>
        </div>
        <div className="flex justify-center mt-4">
          <button
            type="submit"
            onClick={handleInsertDonorStock}
            className="btn-primary mr-4"
          >
            Add Donor
          </button>
          <button type="button" onClick={handleReset} className="btn-secondary">
            Reset
          </button>
        </div>
      </form>
    </>
  );
};

export default AddDonor;
