import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const DonorList = () => {
  const [donors, setDonors] = useState([]);

  useEffect(() => {
    const fetchDonors = async () => {
      try {
        const response = await axios.get('/api/donors');
        setDonors(response.data);
      } catch (error) {
        console.error('Error fetching donors:', error);
      }
    };

    fetchDonors();
  }, []);

  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">All Donors</h2>
      {donors.length === 0 ? (
        <p>No donors available.</p>
      ) : (
        <ul>
          {donors.map((donor) => (
            <li key={donor.premium_donor_id} className="mb-4">
              <strong>Name:</strong> {donor.first_name} {donor.last_name} <br />
              <strong>Email:</strong> {donor.email} <br />
              <strong>Blood Type:</strong> {donor.blood_type} <br />
              {/* Display other donor details as needed */}
              <Link to={`/donor/${donor.premium_donor_id}`} className="text-blue-500 hover:underline">
                View Profile
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default DonorList;
