import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';

const NearbyDonorsMap = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [nearbyDonors, setNearbyDonors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Use navigator.geolocation to get the user's current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation([position.coords.latitude, position.coords.longitude]);
        },
        (error) => {
          console.error('Error getting user location:', error);
          setError('Error getting user location. Please allow location access and refresh the page.');
        }
      );
    }
  }, []);

  useEffect(() => {
    // Fetch nearby premium donors when user location is available
    if (userLocation) {
      fetchNearbyDonors();
    }
  }, [userLocation]);

  const fetchNearbyDonors = async () => {
    try {
      setLoading(true);
      setError(null);

      // Make an API request to fetch nearby premium donors
      const response = await axios.get('/api/donors/nearby', {
        params: {
          latitude: userLocation[0],
          longitude: userLocation[1],
        },
      });

      setNearbyDonors(response.data);
    } catch (error) {
      console.error('Error fetching nearby donors:', error);
      setError('Error fetching nearby donors. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Nearby Premium Donors</h2>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {userLocation && (
        <MapContainer center={userLocation} zoom={13} style={{ height: '500px', width: '100%' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />

          {nearbyDonors.map((donor) => (
            <Marker key={donor.premium_donor_id} position={[donor.latitude, donor.longitude]}>
              <Popup>
                <div>
                  <h3>{donor.user_name}</h3>
                  <p>Availability: {donor.availability_time}</p>
                  {/* Add other donor information as needed */}
                </div>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      )}
    </div>
  );
};

export default NearbyDonorsMap;
