import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useStaff } from './StaffContext'; // Update the import path
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ManageDonations = () => {
  const [donations, setDonations] = useState([]);
  const { state } = useStaff();
  const { stfStaffType } = state;

  const fetchDonations = async () => {
    try {
      const token = localStorage.getItem('token'); // Get authentication token from localStorage
      const response = await axios.get('http://localhost:5000/donations', {
        headers: {
          Authorization: `Bearer ${token}`, // Include authentication token in request headers
        },
      });
      setDonations(response.data);
    } catch (error) {
      console.error('Error fetching donations:', error);
      toast.error('Error fetching donations');
    }
  };

  useEffect(() => {
    // Fetch donations only if the user is logged in and has the right role
    if (state.isLoggedIn && (stfStaffType === 'Admin' || stfStaffType === 'Staff')) {
      fetchDonations();
    }
  }, [state.isLoggedIn, stfStaffType]); // Fetch donations when login status or role changes

  return (
    <div className="container mx-auto px-4 pb-16">
      <h2 className="text-center text-3xl font-bold mb-8">Manage Donations</h2>

      {state.isLoggedIn && (state.stfStaffType === "Staff" || state.stfStaffType === "Admin") ? (
        <ul className="list-group rounded-lg shadow-md">
          {donations.map((donation) => (
            <li
              key={donation.id}
              className="list-group-item d-flex justify-content-between align-items-center border-gray-200 rounded-lg mb-2"
            >
              <div className="flex-grow">
                <strong className="text-primary">{donation.name}</strong>
                <div className="text-muted mt-1 flex flex-wrap">
                  <span className="mr-4">
                    <strong>Age:</strong> {donation.age}
                  </span>
                  <span className="mr-4">
                    <strong>Gender:</strong> {donation.gender}
                  </span>
                  <span className="mr-4">
                    <strong>Blood Group:</strong> {donation.blood_group}
                  </span>
                  <span>
                    <strong>Units:</strong> {donation.units}
                  </span>
                </div>
                <div className="text-muted mt-1 flex flex-wrap">
                  <span className="mr-4">
                    <strong>Disease:</strong> {donation.disease || "N/A"}
                  </span>
                  <span className="mr-4">
                    <strong>Reason:</strong> {donation.reason || "N/A"}
                  </span>
                  <span>
                    <strong>Date:</strong> {donation.date}
                  </span>
                </div>
              </div>
              <span className="font-medium badge bg-warning text-dark rounded-full px-2 py-1">
                {donation.status}
              </span>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-center text-lg mt-8">
          You do not have permission to view this page.
        </p>
      )}
    </div>
  );
};

export default ManageDonations;
