// UserRegister.js
import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import registerImage from '../../Assest/img/Register.png';
import { useUserRole } from './UserContext';

const UserRegister = () => {
  const { setUserRoleContext } = useUserRole();
  const [user, setUser] = useState({
    userName: '',
    userAge: '',
    userGender: '',
    userBloodGroup: '',
    userPhone: '',
    userEmail: '',
    userAddress: '',
    userUserName: '',
    userPassword: '',
    userRole: '',
    userPasswordConfirmation: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      if (!user.userName || !user.userPhone || !user.userEmail || !user.userPassword || !user.userPasswordConfirmation || !user.userRole) {
        setError('Please fill in all required fields.');
        return;
      }

      // Your registration logic...
      const res = await fetch('http://localhost:5000/register/user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(user),
      });

      if (res.ok) {
        // Set user role context based on the selected user role
        setUserRoleContext(user.userRole); // Set the selected user role to the context
        toast.success('Registration successful!');
      } else {
        const data = await res.json();
        toast.error(data.message || 'Registration failed');
      }

      setUser({
        userName: '',
        userAge: '',
        userGender: '',
        userBloodGroup: '',
        userPhone: '',
        userEmail: '',
        userAddress: '',
        userUserName: '',
        userPassword: '',
        userRole: '',
        userPasswordConfirmation: ''
      });
      setError(null);
    } catch (err) {
      console.error(err);
      toast.error(err.message || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="min-h-screen flex items-center justify-center bg-lightBlue-100">
        <div className="w-full max-w-2xl">
          <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
            <div className="mb-6 text-center">
              <img className="mx-auto h-32" src={registerImage} alt="Register" />
            </div>
            <h1 className="text-2xl font-bold mb-4 text-center">User Registration</h1>
            {error && (
              <div className="text-red-500 mb-4" role="alert">
                {error}
              </div>
            )}
            <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-4">
              {/* Form inputs */}
            </form>
            <ToastContainer position="bottom-right" autoClose={5000} hideProgressBar={false} newestOnTop={false} closeOnClick rtl={false} pauseOnFocusLoss draggable pauseOnHover />
          </div>
        </div>
      </div>
    </>
  );
};

export default UserRegister;
