import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const PremiumDonorDetails = () => {
  const { id } = useParams();
  const [donorDetails, setDonorDetails] = useState(null);

  useEffect(() => {
    // Fetch donor details based on the ID
    const fetchDonorDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/donors/${id}`);
        setDonorDetails(response.data);
      } catch (error) {
        console.error('Error fetching donor details:', error);
      }
    };

    fetchDonorDetails();
  }, [id]);

  return (
    <div className="w-1/4 p-4">
      <h2 className="text-2xl font-bold mb-4">Premium Donor Details</h2>
      {donorDetails && (
        <div style={{ background: '#f1f1f1', padding: '10px', borderRadius: '5px' }}>
          <h3>FullName: {donorDetails.first_name} {donorDetails.last_name}</h3>
          <p>Email: {donorDetails.email}</p>
          <p>Blood Type: {donorDetails.blood_type}</p>
          <p>Latitude: {donorDetails.latitude}</p>
          <p>Longitude: {donorDetails.longitude}</p>
          <p>Age: {donorDetails.age}</p>
          <p>Address: {donorDetails.address}</p>
          <p>Phone Number: {donorDetails.phone_number}</p>
          <p>Availability Time: {donorDetails.availability_time}</p>
          <p>Donor Type: {donorDetails.donor_type}</p>
          {donorDetails.profile_picture && donorDetails.profile_picture.data && (
            <img
              src={`data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(donorDetails.profile_picture.data)))}`}
              alt={`Profile of ${donorDetails.first_name} ${donorDetails.last_name}`}
              className="rounded-full h-25 w-40 object-cover"
            />
          )}
        </div>
      )}
    </div>
  );
};

export default PremiumDonorDetails;
