import React, { useState } from 'react';
import axios from 'axios';

const BloodTestForm = () => {
  const [donorInfo, setDonorInfo] = useState({
    name: '',
    general_health: 'good',
    disqualifying_medications: false,
    recent_travel: false,
    recent_tattoos: false,
    recent_sexual_activity: false,
    drug_use: false,
  });

  const [result, setResult] = useState(null);
  const [donorStockList, setDonorStockList] = useState([]);

  useEffect(() => {
    // Fetch donor stock data when the component mounts
    fetchDonorStock();
  }, []);

  const fetchDonorStock = async () => {
    try {
      const response = await axios.get('http://localhost:3001/donor-inventory');
      setDonorStockList(response.data);
    } catch (error) {
      console.error('Error fetching donor stock:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const inputValue = type === 'checkbox' ? checked : value;

    setDonorInfo((prevDonorInfo) => ({
      ...prevDonorInfo,
      [name]: inputValue,
    }));
  };

  const handleCheckBloodSample = async () => {
    try {
      const response = await axios.post('http://localhost:3001/blood-testing/check', {
        donorId: donorInfo.id,  // Add donorId if it's already available
      });

      setResult(response.data);
    } catch (error) {
      console.error('Error checking blood sample:', error);
    }
  };



  return (
    
    <div>
        <h1 className="text-3xl font-bold mb-4">Donor details</h1>
      <table className="w-full border border-collapse">
        <thead>
          <tr className="bg-gray-200">
          <th className="py-2 px-4 border">Donor Name</th>
            <th className="py-2 px-4 border">Blood Group</th>
            <th className="py-2 px-4 border">Unit</th>
            <th className="py-2 px-4 border">Age</th>
            <th className="py-2 px-4 border">Address</th>
            
            <th className="py-2 px-4 border">Donation Time</th>
            <th className="py-2 px-4 border">Blood Status</th>
          </tr>
        </thead>
        <tbody>
          {donorStockList.map((donorStock, index) => (
            <tr key={donorStock.donor_id || index} className="hover:bg-gray-100">
               <td className="py-2 px-4 border">{donorStock.donor_name}</td>
              <td className="py-2 px-4 border">{donorStock.blood_group}</td>
              <td className="py-2 px-4 border">{donorStock.unit}</td>
              <td className="py-2 px-4 border">{donorStock.age}</td>
              <td className="py-2 px-4 border">{donorStock.address}</td>
             
              <td className="py-2 px-4 border">{donorStock.donation_time}</td>
              <td className="py-2 px-4 border">
               
                <button
                  className="bg-red-500 text-white px-2 py-1 rounded"
                  
                >
                  Unknown status
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Blood Test Form</h2>
      <form>
        <label>
          Name:
          <input type="text" name="name" value={donorInfo.name} onChange={handleInputChange} />
        </label>
        <label>
          General Health:
          <select name="general_health" value={donorInfo.general_health} onChange={handleInputChange}>
            <option value="good">Good</option>
            <option value="average">Average</option>
            <option value="poor">Poor</option>
          </select>
        </label>
        <label>
          Disqualifying Medications:
          <input
            type="checkbox"
            name="disqualifying_medications"
            checked={donorInfo.disqualifying_medications}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Recent Travel:
          <input type="checkbox" name="recent_travel" checked={donorInfo.recent_travel} onChange={handleInputChange} />
        </label>
        <label>
          Recent Tattoos:
          <input type="checkbox" name="recent_tattoos" checked={donorInfo.recent_tattoos} onChange={handleInputChange} />
        </label>
        <label>
          Recent Sexual Activity:
          <input
            type="checkbox"
            name="recent_sexual_activity"
            checked={donorInfo.recent_sexual_activity}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Drug Use:
          <input type="checkbox" name="drug_use" checked={donorInfo.drug_use} onChange={handleInputChange} />
        </label>
    
      </form>
      
      <button onClick={handleCheckBloodSample}>Check Blood Sample</button>

      {result && (
        <div>
          <h3>Result:</h3>
          {result.error ? <p>Error: {result.error}</p> : <pre>{JSON.stringify(result.result, null, 2)}</pre>}
        </div>
      )}
    </div>
  );
};

export default BloodTestForm;
