import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CampBloodDonationComponent = () => {
  const [donationList, setDonationList] = useState([]);
  const [newDonation, setNewDonation] = useState({
    donorName: '',
    bloodGroup: '',
    donationDate: '',
    donationTime: '',
    bloodUnit: 0,
  });
  
  const [editingDonationId, setEditingDonationId] = useState(null);

  // Fetch blood donation data when the component mounts
  useEffect(() => {
    axios.get('http://localhost:5000/camp/donations')
      .then(response => {
        setDonationList(response.data);
      })
      .catch(error => console.error('Error fetching blood donations:', error));
  }, []);

  const handleUpdateDonation = (id) => {
    const donationToUpdate = donationList.find(item => item.id === id);
    if (!donationToUpdate) {
      console.error('Blood donation not found for updating.');
      return;
    }

    axios.put(`http://localhost:5000/camp/donations/${id}`, donationToUpdate)
      .then(response => {
        console.log('Blood donation updated successfully:', response.data);
        // Fetch updated data from the server to ensure consistency
        axios.get('http://localhost:5000/camp/donations')
          .then(response => {
            setDonationList(response.data);
            setEditingDonationId(null);
          })
          .catch(error => console.error('Error fetching updated blood donation data:', error));
      })
      .catch(error => console.error('Error updating blood donation:', error));
  };

  const handleEditDonation = (id) => {
    setEditingDonationId(id);
  };

  const handleInsertDonation = () => {
    // Update local state immediately
    setDonationList(prevList => [...prevList, newDonation]);
    // Clear the form
    setNewDonation({
      donorName: '',
      bloodGroup: '',
      donationDate: '',
      donationTime: '',
      bloodUnit: 0,
    });

    // Make the network request in the background
    axios.post('http://localhost:5000/camp/donations/insert', newDonation)
      .then(response => {
        console.log('Blood donation inserted successfully:', response.data);
        // If needed, update the state again based on the response
        // setDonationList(prevList => [...prevList, response.data]);
      })
      .catch(error => console.error('Error inserting blood donation:', error));
  };

  const handleDeleteDonation = id => {
    axios.delete(`http://localhost:5000/camp/donations/${id}`)
      .then(response => {
        console.log('Blood donation deleted successfully:', response.data);
        // Update the donationList state by removing the deleted item
        setDonationList(prevList => prevList.filter(item => item.id !== id));
      })
      .catch(error => console.error('Error deleting blood donation:', error));
  };

  return (
    <div>
      <h1>Camp Blood Donation Management</h1>
      <table>
        <thead>
          <tr>
            <th>Donor Name</th>
            <th>Blood Group</th>
            <th>Donation Date</th>
            <th>Donation Time</th>
            <th>Blood Unit</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {donationList.map((donation, index) => (
            <tr key={donation.id || index}>
              <td>
                {editingDonationId === donation.id ? (
                  <input
                    type="text"
                    value={donation.donorName}
                    onChange={(e) => setDonationList(prevList =>
                      prevList.map(item => (item.id === donation.id ? { ...item, donorName: e.target.value } : item))
                    )}
                  />
                ) : (
                  donation.donorName
                )}
              </td>
              <td>
                {editingDonationId === donation.id ? (
                  <input
                    type="text"
                    value={donation.bloodGroup}
                    onChange={(e) => setDonationList(prevList =>
                      prevList.map(item => (item.id === donation.id ? { ...item, bloodGroup: e.target.value } : item))
                    )}
                  />
                ) : (
                  donation.bloodGroup
                )}
              </td>
              <td>
                {editingDonationId === donation.id ? (
                  <input
                    type="date"
                    value={donation.donationDate}
                    onChange={(e) => setDonationList(prevList =>
                      prevList.map(item => (item.id === donation.id ? { ...item, donationDate: e.target.value } : item))
                    )}
                  />
                ) : (
                  donation.donationDate
                )}
              </td>
              <td>
                {editingDonationId === donation.id ? (
                  <input
                    type="time"
                    value={donation.donationTime}
                    onChange={(e) => setDonationList(prevList =>
                      prevList.map(item => (item.id === donation.id ? { ...item, donationTime: e.target.value } : item))
                    )}
                  />
                ) : (
                  donation.donationTime
                )}
              </td>
              <td>
                {editingDonationId === donation.id ? (
                  <input
                    type="number"
                    value={donation.bloodUnit}
                    onChange={(e) => setDonationList(prevList =>
                      prevList.map(item => (item.id === donation.id ? { ...item, bloodUnit: Number(e.target.value) } : item))
                    )}
                  />
                ) : (
                  donation.bloodUnit
                )}
              </td>
              <td>
                {editingDonationId === donation.id ? (
                  <button onClick={() => handleUpdateDonation(donation.id)}>
                    Save
                  </button>
                ) : (
                  <button onClick={() => handleEditDonation(donation.id)}>
                    Edit
                  </button>
                )}
                <button onClick={() => handleDeleteDonation(donation.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

     
    </div>
  );
};

export default CampBloodDonationComponent;
