import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function DonorDetailsPage() {
  
  const { id } = useParams();

  const [donor, setDonor] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/donors/${id}`)
      .then(res => res.json())
      .then(data => setDonor(data))
  }, [id])

  return (
    <div>
      { donor &&
        <>
          <h1>{donor.firstName} {donor.lastName}</h1>
          <p>{donor.email}</p>
          <p>{donor.phoneNumber}</p>
          <img src={donor.profilePicture} alt={donor.firstName} />
        </>
      }
    </div>
  )
}

export default DonorDetailsPage;
