import React, { useState, useEffect } from 'react';
import './BloodRequestHistory.css'; // Import custom Tailwind CSS styles

function BloodRequestHistory() {
  const [history, setHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHistory = async () => {
      setIsLoading(true);
      setError(null);

      try {
        const response = await fetch('http://localhost:5000/login/stf/history'); // Assuming '/login/stf/history' is your API endpoint
        const data = await response.json();

        setHistory(data);
      } catch (error) {
        setError(error.message || 'An error occurred while fetching history.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchHistory();
  }, []);

  return (
    <div className="container mx-auto px-4 pb-16">
      <h1 className="text-3xl font-bold mb-8">Blood Request History</h1>

      {isLoading && (
        <div className="text-center">Loading history...</div>
      )}

      {error && (
        <div className="text-red-500 text-center font-bold mb-4">{error}</div>
      )}

      {history.length > 0 && (
        <table className="table-auto w-full text-left border-collapse border-gray-200">
          <thead>
            <tr>
              <th className="border px-4 py-2">Blood Group</th>
              <th className="border px-4 py-2">Units Requested</th>
              <th className="border px-4 py-2">Patient Name</th>
              <th className="border px-4 py-2">Patient Address</th>
              <th className="border px-4 py-2">Patient Contact</th>
            </tr>
          </thead>
          <tbody>
            {history.map((request) => (
              <tr key={request.id} className="border-b">
                <td className="border px-4 py-2">{request.blood_group}</td>
                <td className="border px-4 py-2">{request.unit}</td>
                <td className="border px-4 py-2">{request.patient_name}</td>
                <td className="border px-4 py-2">{request.patient_address}</td>
                <td className="border px-4 py-2">{request.patient_contact}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {history.length === 0 && !isLoading && (
        <div className="text-center text-gray-500 mb-4">No blood requests found.</div>
      )}
    </div>
  );
}

export default BloodRequestHistory;
