import React, { useState } from 'react';
import axios from 'axios';

const BloodStockForm = () => {
  // Use consistent naming for clarity
  const [bloodStockData, setBloodStockData] = useState({
    bloodGroup: '', // Capitalize "Group"
    totalUnit: '', // Capitalize "Unit"
    currentStock: '', // Capitalize "Stock"
    bloodStatus: '',
  });

  const handleChange = (event) => {
    setBloodStockData({ ...bloodStockData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('/login/stf/inv/insert', bloodStockData);
      console.log('Blood stock inserted successfully:', response.data);

      // Handle success
      // - Clear the form
      setBloodStockData({
        bloodGroup: '',
        totalUnit: '',
        currentStock: '',
        bloodStatus: '',
      });
      // - Show a success message to the user
    } catch (error) {
      console.error('Error during blood stock insertion:', error);

      // Handle error
      // - Show an error message to the user with specific details
    }
  };

  return (
    <div className="container mx-auto bg-white shadow-md rounded-md p-8">
      <h1 className="text-4xl font-bold mt-5 mb-8 text-center text-red-500">Blood Stock Form</h1>
      <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-gray-200 rounded-md">
        <div className="mb-4">
          <label htmlFor="blood_group" className="block text-sm font-medium text-gray-600">
            Blood Group
          </label>
          <input
            type="text"
            id="blood_group"
            name="blood_group"
            value={bloodStockData.bloodGroup}
            onChange={handleChange}
            className="input-field w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-1 focus:ring-red-500 focus:border-red-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="total_unit" className="block text-sm font-medium text-gray-600">
            Total Unit
          </label>
          <input
            type="number"
            id="total_unit"
            name="total_unit"
            value={bloodStockData.totalUnit}
            onChange={handleChange}
            className="input-field w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-1 focus:ring-red-500 focus:border-red-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="current_stock" className="block text-sm font-medium text-gray-600">
            Current Stock
          </label>
          <input
            type="number"
            id="current_stock"
            name="current_stock"
            value={bloodStockData.currentStock}
            onChange={handleChange}
            className="input-field w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-1 focus:ring-red-500 focus:border-red-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="blood_status" className="block text-sm font-medium text-gray-600">
            Blood Status
          </label>
          <input
            type="text"
            id="blood_status"
            name="blood_status"
            value={bloodStockData.bloodStatus}
            onChange={handleChange}
            className="input-field w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-1 focus:ring-red-500 focus:border-red-500"
            
            required
          />
        </div>

        <button type="submit" className="submit-button bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
          Submit
        </button>
      </form>

      <div className="mt-8">
        <h2 className="text-lg font-bold">Adding Blood Inventory</h2>
        <p className="text-gray-600">
          This form is for adding blood stock to the inventory.
        </p>
      </div>
    </div>
  );
};

export default BloodStockForm;
