import React from "react";
import { Link } from 'react-router-dom';
import { useUser } from '../components/Rent/UserContext';
import logo from './../Assest/img/logo.png';

const Navbar = () => {
  const { state, logout } = useUser();
  const isAuthenticated = state.isAuthenticated;

  const handleLogout = () => {
    logout();
  };

  return (
    <div className="bg-pastel-green p-4 sticky top-0 z-50 flex justify-between items-center">
      <Link to="/">
        <img className="logo" src={logo} alt="Logo" />
        <div className="text-white font-bold text-xl ml-2">blood care Nexus</div>
      </Link>

      <ul className="flex space-x-4">
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/events">Events</Link>
        </li>
        <li>
          <Link to="/donor">Donor</Link>
        </li>
        <li>
          <Link to="/blog">Blog</Link>
        </li>
        <li>
          <Link to="/blood-recipient">Blood Recipient</Link>
        </li>
        <li>
          <Link to="/staff">Staff</Link>
        </li>
        <li>
          <Link to="/blood-bank">Blood Bank</Link>
        </li>
      </ul>

      {isAuthenticated ? (
        <div className="flex items-center">
          <Link to="/rent/rentprofile" className="text-white font-bold ml-4">
            {state.username || 'Profile'}
          </Link>
          <button onClick={handleLogout} className="ml-4 text-white font-bold">
            Logout
          </button>
        </div>
      ) : (
        <div className="flex items-center">
          <Link to="/rent/signup" className="text-white font-bold ml-4">
            Sign In
          </Link>
          <Link to="/rent/login" className="ml-4 text-white font-bold">
            Join Now
          </Link>
        </div>
      )}
    </div>
  );
};

export default Navbar;
