import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Route, Routes, Link, useNavigate } from 'react-router-dom';


// DonorListItem Component
// DonorListItem Component
// const DonorListItem = ({ donor, onViewProfile }) => (
//   <div key={donor.donor_id} className="mb-2" style={{ background: 'white', padding: '10px', borderRadius: '5px' }}>
//     <h3>FullName: {donor.first_name} {donor.last_name}</h3>
//     <button
//       className="ml-2 bg-blue-500 text-white px-2 py-1 rounded"
//       onClick={() => onViewProfile(donor.premium_donor_id)}  
//     >
//       View Profile
//     </button>
//   </div>
// );


const PremiumDonorManagement = () => {
  const [premiumDonors, setPremiumDonors] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch premium donors data from your API or server
    fetch('http://localhost:5000/api/donors')  // Use your specific API endpoint
      .then(response => response.json())
      .then(data => {
        setPremiumDonors(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching premium donors:', error);
        setLoading(false);
      });
  }, []); // Empty dependency array ensures useEffect runs only once on component mount

  return (
    <div>
      <h2>Premium Donors List</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Action</th>
              {/* Add more columns based on your premium donor data */}
            </tr>
          </thead>
          <tbody>
            {premiumDonors.map(donor => (
              <tr key={donor.premium_donor_id}>
                <td>{donor.premium_donor_id}</td>
                <td>{donor.first_name}</td>
                <td>{donor.last_name}</td>
                <td>{donor.email}</td>
                <td>
                  <Link to={`/donors/${donor.premium_donor_id}`}>
                    <button>View Profile</button>
                  </Link>
                </td>
                {/* Add more cells based on your premium donor data */}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PremiumDonorManagement;
