import React, { useState } from 'react';
import axios from 'axios';

const DonorDetails = ({ donorStockList }) => {
  const [selectedDonor, setSelectedDonor] = useState(null);
  const [formValues, setFormValues] = useState({
    generalHealth: 'good',
    disqualifyingMedications: 'no',
    recentTravel: 'no',
    recentTattoos: 'no',
    recentSexualActivity: 'no',
    drugUse: 'no',
    bloodStatus: 'safe',
  });

  const handleCheckBlood = async (donorId, formValues) => {
    try {
      const response = await axios.post('/test-blood', {
        donorId,
        ...formValues,
      });

      if (response.data.result === 'safe') {
        // Handle safe blood condition
        console.log('Blood is safe:', response.data.donor);
      } else {
        // Handle unsafe blood condition
        console.log('Blood is unsafe:', response.data.donor);
      }

      // Optionally, you can update your component state or perform other actions based on the API response.
    } catch (error) {
      // Handle API call errors
      console.error('Error testing blood:', error.message);
    }
  };
  const handleFormSubmit = async () => {
    try {
      // Call the API endpoint with the selected donor ID and form values
      if (selectedDonor) {
        await handleCheckBlood(selectedDonor.donor_id, formValues);
  
        // Example: Clear the form values and update the component state after form submission
        setFormValues({
          generalHealth: 'good',
          disqualifyingMedications: 'no',
          recentTravel: 'no',
          recentTattoos: 'no',
          recentSexualActivity: 'no',
          drugUse: 'no',
          bloodStatus: 'safe',
        });
  
        // Additional logic if needed
      }
    } catch (error) {
      // Handle form submission errors
      console.error('Error submitting form:', error.message);
    }
  };
  

  return (
    <div>
      <h1 className="text-3xl font-bold mb-4">Donor details</h1>
      <table className="w-full border border-collapse">
        <thead>
          <tr className="bg-gray-200">
            <th className="py-2 px-4 border">Donor Name</th>
            <th className="py-2 px-4 border">Blood Group</th>
            <th className="py-2 px-4 border">Unit</th>
            <th className="py-2 px-4 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {donorStockList && donorStockList.length > 0 ? (
            donorStockList.map((donorStock, index) => (
              <tr key={donorStock.donor_id || index} className="hover:bg-gray-100">
                <td className="py-2 px-4 border">{donorStock.donor_name}</td>
                <td className="py-2 px-4 border">{donorStock.blood_group}</td>
                <td className="py-2 px-4 border">{donorStock.unit}</td>
                <td className="py-2 px-4 border">
                  <button onClick={() => setSelectedDonor(donorStock)}>Check Blood</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No donor stock available</td>
            </tr>
          )}
        </tbody>
      </table>

      {selectedDonor && (
        <div>
          <h2>Check Blood Conditions for {selectedDonor.donor_name}</h2>
          <form>
            <label>
              General Health:
              <select value={formValues.generalHealth} onChange={(e) => setFormValues({ ...formValues, generalHealth: e.target.value })}>
                <option value="good">Good</option>
                {/* Add more options as needed */}
              </select>
            </label>
            <label>
              Disqualifying Medications:
              <select value={formValues.disqualifyingMedications} onChange={(e) => setFormValues({ ...formValues, disqualifyingMedications: e.target.value })}>
                <option value="no">No</option>
                {/* Add more options as needed */}
              </select>
            </label>
            {/* Add similar labels and selects for other conditions */}
            <label>
              Blood Status:
              <select value={formValues.bloodStatus} onChange={(e) => setFormValues({ ...formValues, bloodStatus: e.target.value })}>
                <option value="safe">Safe</option>
                <option value="unsafe">Unsafe</option>
              </select>
            </label>
            <button type="button" onClick={handleFormSubmit}>
              Submit
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default DonorDetails;
