// Sidebar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUsers, faUser, faVial, faSignOutAlt, faCog } from '@fortawesome/free-solid-svg-icons';

const Sidebar = () => {
  const [showDonorManagementDropdown, setShowDonorManagementDropdown] = useState(false);

  const toggleDonorManagementDropdown = () => {
    setShowDonorManagementDropdown(!showDonorManagementDropdown);
  };

  return (
    <div className="bg-gray-800 text-white w-1/6 p-4 h-screen fixed">
      <div className="mb-8">
        {/* Sidebar Logo or Name */}
        <p className="text-2xl font-bold">BloodMS</p>
      </div>
      <div className="mb-4">
        {/* Sidebar Navigation Links */}
        <Link to="/staff-home" className="flex items-center space-x-2 py-2">
          <FontAwesomeIcon icon={faHome} />
          <span>Home</span>
        </Link>
        <div className="flex items-center space-x-2 py-2 cursor-pointer" onClick={toggleDonorManagementDropdown}>
          <FontAwesomeIcon icon={faUsers} />
          <span>Donor Management</span>
        </div>
        {/* Donor Management Dropdown */}
        {showDonorManagementDropdown && (
          <div className="pl-6">
            <Link to="/register-donor" className="block py-2">
              Register Donor
            </Link>
            <Link to="/view-donation-history" className="block py-2">
              View Donation History
            </Link>
            <Link to="/manage-donor" className="block py-2">
              Manage Donor
            </Link>
          </div>
        )}
        <Link to="/recipient-management" className="flex items-center space-x-2 py-2">
          <FontAwesomeIcon icon={faUser} />
          <span>Recipient Management</span>
        </Link>
        <Link to="/blood-testing" className="flex items-center space-x-2 py-2">
          <FontAwesomeIcon icon={faVial} />
          <span>Blood Testing</span>
        </Link>
      </div>
      <div className="mt-auto">
        {/* Sidebar Logout and Settings */}
        <Link to="/logout" className="flex items-center space-x-2 py-2">
          <FontAwesomeIcon icon={faSignOutAlt} />
          <span>Logout</span>
        </Link>
        <Link to="/settings" className="flex items-center space-x-2 py-2">
          <FontAwesomeIcon icon={faCog} />
          <span>Settings</span>
        </Link>
      </div>
    </div>
  );
};

export default Sidebar;
