import React, { useState } from 'react';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import LoginImage from '../../Assest/img/LoginPage.jpg';

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/user/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        toast('Login successful!', { type: 'success' });
      } else {
        toast('Invalid credentials!', { type: 'error' });
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="login-page">
      <div className="login-form-container p-4" style={{ backgroundColor: '#f0f0f0', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
        <h1 className='font-bold text-lg text-midnight dark:text-white-900 mb-2'>User Login</h1>

        <form onSubmit={handleSubmit} className="grid gap-4">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />

          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />

          <button type="submit" className="bg-green-500 text-white p-2 rounded hover:bg-green-600">
            Login
          </button>

          <div className="footnote text-center">
            <a href="#" className="text-blue-500 hover:underline">
              Forgot password?
            </a>
          </div>
        </form>
      </div>

      <div className="login-image-container mt-4 text-center">
        <img src={LoginImage} alt="Login" style={{ width: '50px', height: '50px' }} />
      </div>
    </div>
  );
}

export default LoginPage;
