import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';

// Component definition
const NearbyDonorsMap = () => {
  const [donorsLocations, setDonorsLocations] = useState([]);

  useEffect(() => {
    // Fetch donor locations from the server in a secure way, e.g., using authentication and proper authorization
    axios.get('http://localhost:5000/api/premium-donors/locations')
      .then(response => setDonorsLocations(response.data))
      .catch(error => console.error('Error fetching donor locations:', error));
  }, []);

  // Replace with a default latitude and longitude that does not reveal any personal information
  const defaultLat = 27.7172;
  const defaultLng = 85.3236;

  return (
    <div>
      <h2>Donors Map</h2>
      <MapContainer center={[defaultLat, defaultLng]} zoom={13} style={{ height: '400px', width: '100%' }}>
        {/* Use a responsive design with 100% width for the map container */}
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        {
          donorsLocations.map(donor => (
            <Marker key={donor.premium_donor_id} position={[donor.latitude, donor.longitude]}>
              {/* Popup content can be extended with additional donor information */}
              <Popup>
                <strong>{donor.userName}</strong><br />
                Phone: {donor.userPhone}
              </Popup>
            </Marker>
          ))
        }
      </MapContainer>
    </div>
  );
};

export default NearbyDonorsMap;
