import React, { useState, useEffect } from 'react';
import axios from 'axios';

import './BloodInventory.css'

const BloodStockComponent = () => {
  const [bloodStockList, setBloodStockList] = useState([]);
  const [newBloodStock, setNewBloodStock] = useState({
    blood_group: '',
    total_unit: 0,
    current_stock: 0,
    blood_status: '',
  });
  const [showForm, setShowForm] = useState(false);

  // Fetch blood stock data when the component mounts
  useEffect(() => {
    axios.get('http://localhost:5000/login/stf/inv')
      .then(response => {
        setBloodStockList(response.data);
      })
      .catch(error => console.error('Error fetching blood stock:', error));
  }, []);

  const handleUpdateBloodStock = (id, updatedBloodStock) => {
    axios.put(`http://localhost:5000/login/stf/inv/update/${id}`, updatedBloodStock)
      .then(response => {
        console.log('Blood stock updated successfully:', response.data);
        // Update the bloodStockList state with the updated data if needed
        setBloodStockList(prevList =>
          prevList.map(item => (item.id === id ? { ...item, ...updatedBloodStock } : item))
        );
      })
      .catch(error => console.error('Error updating blood stock:', error));
  };

  const handleInsertBloodStock = () => {
    axios.post('http://localhost:5000/login/stf/inv/insert', newBloodStock)
      .then(response => {
        console.log('Blood stock inserted successfully:', response.data);
        // Update the bloodStockList state with the new data if needed
        setBloodStockList(prevList => [...prevList, response.data]);
        // Hide the form after successful insertion
        setShowForm(false);
      })
      .catch(error => console.error('Error inserting blood stock:', error));
  };

  const handleDeleteBloodStock = id => {
    axios.delete(`http://localhost:5000/login/stf/inv/delete/${id}`)
      .then(response => {
        console.log('Blood stock deleted successfully:', response.data);
        // Update the bloodStockList state by removing the deleted item if needed
        setBloodStockList(prevList => prevList.filter(item => item.id !== id));
      })
      .catch(error => console.error('Error deleting blood stock:', error));
  };

  return (
    <div>
      <h1>Blood Stock Management</h1>

      <h2>Existing Blood Stock</h2>
      <table>
        <thead>
          <tr>
            <th>Blood Group</th>
            <th>Total Units</th>
            <th>Current Stock</th>
            <th>Blood Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {bloodStockList.map(bloodStock => (
            <tr key={bloodStock.id}>
              <td>{bloodStock.blood_group}</td>
              <td>{bloodStock.total_unit}</td>
              <td>{bloodStock.current_stock}</td>
              <td>{bloodStock.blood_status}</td>
              <td>
                <button onClick={() => handleUpdateBloodStock(bloodStock.id, {
                  total_unit: newBloodStock.total_unit,
                  current_stock: newBloodStock.current_stock,
                  blood_status: newBloodStock.blood_status,
                })}>
                  Update
                </button>
                <button onClick={() => handleDeleteBloodStock(bloodStock.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showForm ? (
        <div>
          <h2>Add New Blood Stock</h2>
          <form onSubmit={e => { e.preventDefault(); handleInsertBloodStock(); }}>
            <label>Blood Group:
              <input type="text" value={newBloodStock.blood_group} onChange={e => setNewBloodStock({ ...newBloodStock, blood_group: e.target.value })} />
            </label>
            <label>Total Units:
              <input type="number" value={newBloodStock.total_unit} onChange={e => setNewBloodStock({ ...newBloodStock, total_unit: e.target.value })} />
            </label>
            <label>Current Stock:
              <input type="number" value={newBloodStock.current_stock} onChange={e => setNewBloodStock({ ...newBloodStock, current_stock: e.target.value })} />
            </label>
            <label>Blood Status:
              <input type="text" value={newBloodStock.blood_status} onChange={e => setNewBloodStock({ ...newBloodStock, blood_status: e.target.value })} />
            </label>
            <button type="submit">Add Blood Stock</button>
          </form>
        </div>
      ) : (
        <button onClick={() => setShowForm(true)}>Show Add Blood Stock Form</button>
      )}
    </div>
  );
};

export default BloodStockComponent;
