import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserNavbar from "./UserNavbar";

const DonorStockController = () => {
  const [donorStockList, setDonorStockList] = useState([]);
  const [editingDonorStock, setEditingDonorStock] = useState(null);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    getDonorStocks();
  }, []);

  const getDonorStocks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/login/stf/ds');
      setDonorStockList(response.data);
    } catch (error) {
      console.error('Error fetching donor stocks:', error);
    }
  };

  const handleUpdateDonorStock = (donor_id) => {
    const donorStockToUpdate = donorStockList.find((item) => item.donor_id === donor_id);
    if (!donorStockToUpdate) {
      console.error('Donor stock not found for updating.');
      return;
    }

    // Set the editing donor and show the form
    setEditingDonorStock(donorStockToUpdate);
    setShowForm(true);
  };

  const handleCancelUpdate = () => {
    // Clear the editing donor and hide the form
    setEditingDonorStock(null);
    setShowForm(false);
  };

  const handleSaveUpdate = () => {
    // Use axios to send the updated data to the server
    axios.put(`http://localhost:5000/login/stf/ds/update/${editingDonorStock.donor_id}`, editingDonorStock)
      .then((response) => {
        console.log('Donor stock updated successfully:', response.data);
        // Refresh the entire list from the server after update
        getDonorStocks();
        // Clear the editing donor and hide the form
        setEditingDonorStock(null);
        setShowForm(false);
      })
      .catch((error) => console.error('Error updating donor stock:', error));
  };

  const handleDeleteDonorStock = (donor_id) => {
    axios
      .delete(`http://localhost:5000/login/stf/ds/delete/${donor_id}`)
      .then((response) => {
        console.log('Donor stock deleted successfully:', response.data);
        // Refresh the entire list from the server after deletion
        getDonorStocks();
      })
      .catch((error) => console.error('Error deleting donor stock:', error));
  };

  const handleAddDonorStock = () => {
    // Use axios to send the new donor data to the server
    axios.post('http://localhost:5000/login/stf/ds/insert', newDonorStock)
      .then((response) => {
        console.log('Donor stock added successfully:', response.data);
        // Refresh the entire list from the server after addition
        getDonorStocks();
        // Clear the new donor data and hide the form
        setNewDonorStock({
          blood_group: '',
          unit: 0,
          age: 0,
          address: '',
          donor_name: '',
          donation_time: '',
        });
        setShowForm(false);
      })
      .catch((error) => console.error('Error adding new donor stock:', error));
  };

  return (
    <>
      <UserNavbar />
<div className="container mx-auto p-4">
  <div className="bg-green text-white py-16 mb-8">
    <div className="max-w-2xl mx-auto">
      <h1 className="text-4xl md:text-5xl font-bold mb-4">Donor Management</h1>
      <p className="text-lg md:text-xl"><em>Empowering Lives, Saving Futures</em></p>
    </div>
  </div>

  <table className="w-full border border-collapse">
    <thead>
      <tr className="bg-gray-200">
        <th className="py-2 px-4 border">Donor Name</th>
        <th className="py-2 px-4 border">Blood Group</th>
        <th className="py-2 px-4 border">Unit</th>
        <th className="py-2 px-4 border">Age</th>
        <th className="py-2 px-4 border">Address</th>
        <th className="py-2 px-4 border">Donation Time</th>
        <th className="py-2 px-4 border">Action</th>
      </tr>
    </thead>
    <tbody>
      {donorStockList.map((donorStock, index) => (
        <tr key={donorStock.donor_id || index} className="hover:bg-gray-100">
          <td className="py-2 px-4 border">{donorStock.donor_name}</td>
          <td className="py-2 px-4 border">{donorStock.blood_group}</td>
          <td className="py-2 px-4 border">{donorStock.unit}</td>
          <td className="py-2 px-4 border">{donorStock.age}</td>
          <td className="py-2 px-4 border">{donorStock.address}</td>
          <td className="py-2 px-4 border">{donorStock.donation_time}</td>
          <td className="py-2 px-4 border">
            <button
              className="bg-blue-500 text-white px-2 py-1 rounded mr-2"
              onClick={() => handleUpdateDonorStock(donorStock.donor_id)}
            >
              Update
            </button>
            <button
              className="bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => handleDeleteDonorStock(donorStock.donor_id)}
            >
              Delete
            </button>
          </td>
        </tr>
      ))}
    </tbody>
  </table>

  {/* Add the Update Form */}
  {showForm && (
    <div className="mt-4">
      <h2 className="text-xl font-bold mb-2">{editingDonorStock ? 'Update Donor' : 'Add New Donor'}</h2>
      <form>
        {/* ... Form input fields ... */}
<input
  type="text"
  placeholder="Donor Name"
  value={editingDonorStock ? editingDonorStock.donor_name : newDonorStock.donor_name}
  onChange={(e) => {
    if (editingDonorStock) {
      setEditingDonorStock({ ...editingDonorStock, donor_name: e.target.value });
    } else {
      setNewDonorStock({ ...newDonorStock, donor_name: e.target.value });
    }
  }}
  className="border rounded px-2 py-1"
/>

<input
  type="text"
  placeholder="Blood Group"
  value={editingDonorStock ? editingDonorStock.blood_group : newDonorStock.blood_group}
  onChange={(e) => {
    if (editingDonorStock) {
      setEditingDonorStock({ ...editingDonorStock, blood_group: e.target.value });
    } else {
      setNewDonorStock({ ...newDonorStock, blood_group: e.target.value });
    }
  }}
  className="border rounded px-2 py-1"
/>

<input
  type="number"
  placeholder="Age"
  value={editingDonorStock ? editingDonorStock.age : newDonorStock.age}
  onChange={(e) => {
    if (editingDonorStock) {
      setEditingDonorStock({ ...editingDonorStock, age: e.target.value });
    } else {
      setNewDonorStock({ ...newDonorStock, age: e.target.value });
    }
  }}
  className="border rounded px-2 py-1"
/>
<input
  type="text"
  placeholder="Address"
  value={editingDonorStock ? editingDonorStock.address : newDonorStock.address}
  onChange={(e) => {
    if (editingDonorStock) {
      setEditingDonorStock({ ...editingDonorStock, address: e.target.value });
    } else {
      setNewDonorStock({ ...newDonorStock, address: e.target.value });
    }
  }}
  className="border rounded px-2 py-1"
/>

<input
  type="text"
  placeholder="Donation Time"
  value={editingDonorStock ? editingDonorStock.donation_time : newDonorStock.donation_time}
  onChange={(e) => {
    if (editingDonorStock) {
      setEditingDonorStock({ ...editingDonorStock, donation_time: e.target.value });
    } else {
      setNewDonorStock({ ...newDonorStock, donation_time: e.target.value });
    }
  }}
  className="border rounded px-2 py-1"
/>

        {editingDonorStock ? (
          <button
            className="bg-blue-500 text-white px-2 py-1 rounded mr-2"
            type="button"
            onClick={handleSaveUpdate}
          >
            Save
          </button>
        ) : (
          <button
            className="bg-green-500 text-white px-2 py-1 rounded mr-2"
            type="button"
            onClick={handleAddDonorStock}
          >
            Add Donor
          </button>
        )}
        <button
          className="bg-gray-500 text-white px-2 py-1 rounded"
          type="button"
          onClick={handleCancelUpdate}
        >
          Cancel
        </button>
      </form>
    </div>
  )}
</div>

    </>
  );
};

export default DonorStockController;
