// PremiumDonorManagement.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Route, Routes, Link, useParams, useNavigate } from 'react-router-dom';
import DonorDetails from './DonorDetails';

const PremiumDonorManagement = () => {
  const [premiumDonors, setPremiumDonors] = useState([]);
  const [selectedDonor, setSelectedDonor] = useState(null);
  const [updatedDonor, setUpdatedDonor] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bloodType: '',
    latitude: 0,
    longitude: 0,
    age: 0,
    address: '',
    phoneNumber: '',
    availabilityTime: '',
    DonorType: '',
    // ... (other state properties)
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetchPremiumDonors();
  }, []);

  const fetchPremiumDonors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/donors');
      setPremiumDonors(response.data);
    } catch (error) {
      console.error('Error fetching premium donors:', error);
    }
  };

  const handleViewProfile = async (donor) => {
    try {
      setSelectedDonor(donor);
      clearUpdatedDonor();
  
      // Ensure that premium_donor_id is defined and not null
      if (donor && donor.premium_donor_id) {
        // Navigate to the details page for the selected donor
        await navigate(`/details/${donor.premium_donor_id}`);
      } else {
        console.error('Invalid premium_donor_id:', donor && donor.premium_donor_id);
      }
    } catch (error) {
      console.error('Error navigating to donor details:', error);
    }
  };
  
  
  const clearSelectedDonor = () => {
    setSelectedDonor(null);
    setUpdatedDonor({
      firstName: '',
      lastName: '',
      email: '',
      bloodType: '',
      latitude: 0,
      longitude: 0,
      age: 0,
      address: '',
      phoneNumber: '',
      availabilityTime: '',
      DonorType: '',
      // ... (other state properties)
    });
  };

  const handleUpdateDonor = async () => {
    try {
      await axios.put(`http://localhost:5000/api/donors/update/${selectedDonor.donor_id}`, updatedDonor);
      await handleViewProfile(selectedDonor.premium_donor_id);
    } catch (error) {
      console.error('Error updating donor details:', error);
    }
  };

  return (
    <div className="flex">
      {/* Donor List */}
      <div className="w-1/4 p-4">
        <h2 className="text-2xl font-bold mb-4">Premium Donors</h2>
        <div style={{ height: '4000px', width: '1000px', background: '#f1f1f1', padding: '10px', borderRadius: '5px' }}>
          {premiumDonors.map((donor) => (
            <div key={donor.donor_id} className="mb-2" style={{ background: 'white', padding: '10px', borderRadius: '5px' }}>
              <h3>FullName: {donor.first_name} {donor.last_name}</h3>
              <Link 
                to={`/details/${donor.premium_donor_id}`}
                onClick={() => handleViewProfile(donor.premium_donor_id)}
                className="ml-2 bg-blue-500 text-white px-2 py-1 rounded"
              >
                View Profile
              </Link>
            </div>
          ))}
        </div>
      </div>

      {/* Donor Profile and Update Form */}
      <div className="w-3/4 p-4">
      <Routes>
  <Route path="/details/:id" element={<DonorDetails />} />
</Routes>

      </div>
    </div>
  );
};

export default PremiumDonorManagement;
