import React, { useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify'; 
import 'react-toastify/dist/ReactToastify.css';
import { useUser } from './Usercontext'; // Importing the useUser hook

const DonationForm = () => {
  const { state: { userRole } } = useUser(); // Extract userRole from the UserProvider context
  const [formData, setFormData] = useState({
    bankId: '',
    name: '',
    age: '',
    gender: '',
    bloodGroup: '',
    units: '',
    disease: '',
    reason: '',
    status: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('`http://localhost:5000/donation', formData);
      console.log(response.data);
      toast.success('Donation successful');
      // You can handle success message or redirect to another page here
    } catch (error) {
      console.error('Error:', error.response.data);
      toast.error(error.response.data.message);
      // Handle error, display error message, etc.
    }
  };

  return (
    <div>
      <h2>Donation Form</h2>
      {userRole === 'Donor' ? ( // Check if the user is a donor
        <form onSubmit={handleSubmit}>
          <input type="text" name="bankId" placeholder="Blood Bank ID" onChange={handleChange} required />
          <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
          <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
          <input type="text" name="gender" placeholder="Gender" onChange={handleChange} required />
          <input type="text" name="bloodGroup" placeholder="Blood Group" onChange={handleChange} required />
          <input type="number" name="units" placeholder="Units of Blood" onChange={handleChange} required />
          <input type="text" name="disease" placeholder="Disease (if any)" onChange={handleChange} required />
          <input type="text" name="reason" placeholder="Reason for Donation" onChange={handleChange} required />
          <input type="text" name="status" placeholder="Status" onChange={handleChange} required />
          <button type="submit">Donate</button>
        </form>
      ) : (
        <p>You are not authorized to access this form.</p>
      )}
      <ToastContainer />
    </div>
  );
};

export default DonationForm;
