import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AllDonationsComponent = () => {
  const [donations, setDonations] = useState([]);
  const [error, setError] = useState(null);
  const [updateData, setUpdateData] = useState({
    donorName: '',
    bloodGroup: '',
    donationDate: '',
    donationTime: '',
    bloodUnit: '',
  });

  const fetchAllDonations = async () => {
    try {
      const response = await axios.get('http://localhost:5000/camp/donations');
      setDonations(response.data);
    } catch (err) {
      setError('Error fetching donations');
      console.error(err);
    }
  };

  useEffect(() => {
    fetchAllDonations();
  }, []);

  const handleUpdate = async (donationId) => {
    try {
      await axios.put(`http://localhost:5000/camp/donations/${donationId}`, updateData);
      // Refresh the donations after updating
      fetchAllDonations();
      // Reset the updateData state after updating
      setUpdateData({
        donorName: '',
        bloodGroup: '',
        donationDate: '',
        donationTime: '',
        bloodUnit: '',
      });
    } catch (err) {
      setError('Error updating donation');
      console.error(err);
    }
  };

  const handleDelete = async (donationId) => {
    const confirmDeletion = window.confirm('Are you sure you want to delete this donation?');

    if (!confirmDeletion) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/camp/donations/${donationId}`);
      // Refresh the donations after deleting
      fetchAllDonations();
    } catch (err) {
      setError('Error deleting donation');
      console.error(err);
    }
  };

  return (
    <div>
      <h2>All Donations</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {donations.map((donation) => (
          <li key={donation.id} className="mb-4 p-4 border rounded">
            <div className="mb-2">
              <span className="font-bold">Donor:</span>
              <input
                type="text"
                value={updateData.donorName}
                onChange={(e) => setUpdateData({ ...updateData, donorName: e.target.value })}
                className="border border-gray-300 p-2 ml-2"
              />
            </div>
            <div className="mb-2">
              <span className="font-bold">Blood Group:</span>
              <input
                type="text"
                value={updateData.bloodGroup}
                onChange={(e) => setUpdateData({ ...updateData, bloodGroup: e.target.value })}
                className="border border-gray-300 p-2 ml-2"
              />
            </div>
            <div className="mb-2">
              <span className="font-bold">Date:</span>
              <input
                type="text"
                value={updateData.donationDate}
                onChange={(e) => setUpdateData({ ...updateData, donationDate: e.target.value })}
                className="border border-gray-300 p-2 ml-2"
              />
            </div>
            <div className="mb-2">
              <span className="font-bold">Time:</span>
              <input
                type="text"
                value={updateData.donationTime}
                onChange={(e) => setUpdateData({ ...updateData, donationTime: e.target.value })}
                className="border border-gray-300 p-2 ml-2"
              />
            </div>
            <div className="mb-2">
              <span className="font-bold">Units:</span>
              <input
                type="text"
                value={updateData.bloodUnit}
                onChange={(e) => setUpdateData({ ...updateData, bloodUnit: e.target.value })}
                className="border border-gray-300 p-2 ml-2"
              />
            </div>
            <div className="flex mt-2">
              <button
                onClick={() => handleUpdate(donation.id)}
                className="bg-blue-500 text-white px-4 py-2 mr-2 rounded hover:bg-blue-700"
              >
                Update
              </button>
              <button
                onClick={() => handleDelete(donation.id)}
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllDonationsComponent;
