import React, { useEffect, useState } from 'react';

const NearbyBloodBanks = () => {
  const [bloodBanks, setBloodBanks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let isMounted = true; // Flag to check if the component is mounted

    const fetchNearbyBloodBanks = async () => {
      try {
        const position = await getCurrentPosition();
        const response = await fetch(`http://localhost:5000/login/stf/inv/nearby?radius=10&userLatitude=${position.latitude}&userLongitude=${position.longitude}`);
        
        if (!isMounted) {
          // If the component is unmounted, don't update the state
          return;
        }

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setBloodBanks(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching nearby blood banks:', error);
        if (isMounted) {
          setError('Error fetching nearby blood banks. Please try again.');
          setLoading(false);
        }
      }
    };

    fetchNearbyBloodBanks();

    return () => {
      // Cleanup function to set isMounted to false when the component is unmounted
      isMounted = false;
    };
  }, []);

  const getCurrentPosition = () => {
    return new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(
        (position) => resolve({ latitude: position.coords.latitude, longitude: position.coords.longitude }),
        (error) => reject(error)
      );
    });
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h2>Nearby Blood Banks</h2>
      <ul>
        {bloodBanks.map((bank) => (
          <li key={bank.id}>{bank.name} - {bank.distance} km away</li>
        ))}
      </ul>
    </div>
  );
};

export default NearbyBloodBanks;
